function fn_initForm()
{

	//*------------------------------------------------------------------------
    //* 리사이즈 컨포넌트 정의
	//* H: 가로 리사이즈
	//* W: 세로 리사이즈
	//* A: 가로세로 리사이즈
	//* R: Resize
	//* M: Move
	//* Quick view로 테스트시 주석처리 후 테스트
    //*------------------------------------------------------------------------
	//gfn_SetResize(this, "Shape_search:H:R Shape_bnd:H:R GRD_AFFAIR_MST:H:R GRD_AFFAIR_DTL:A:R btn_add_dtl:H:M btn_del_dtl:H:M");

	gfn_SetResize(this, "GRD_BONDSPOT_LST:H:R GRD_AFFAIRDELAY_LST:H:R ");



	// 진행상태
	gfn_comCodeCombo(S_PRS_STS_Y, "AD04", "","전체");
	gfn_comCodeCombo(COB_PRS_STS_Y, "AD04", "","");

	gfn_comCodeCombo(COB_ACP_MTD_N, "AC09", "","");
	gfn_comCodeCombo(COB_ACP_MTD_Y, "AC09", "","");
	gfn_comCodeCombo(S_ACP_MTD_Y, "AC09", "","전체");
	gfn_comCodeCombo(S_ACP_MTD_DLY, "AC09", "","전체");


	// 채권사진행상태
	gfn_makeCodeDataSet("AD04", "", "DS_PRS_STS");   

	gfn_setGridCombo(GRD_AFF_PRSYES_TREE, "DS_PRS_STS", 14);

	// 접수방법
	gfn_makeCodeDataSet("AC09", "", "DS_ACP_MTD");   

	gfn_setGridCombo(GRD_BONDSPOT_LST, "DS_ACP_MTD", 2);
	gfn_setGridCombo(GRD_AFF_PRSYES_TREE, "DS_ACP_MTD", 10);
	gfn_setGridCombo(GRD_AFFAIRDELAY_LST, "DS_ACP_MTD", 8);

	// 접수처
	gfn_comCodeCombo(COB_ACP_PLC_N, "AC14", "","");
	gfn_comCodeCombo(COB_ACP_PLC_Y, "AC14", "","");

	// 공통 명령어 조정
	gfn_set_cbutton("조회:fn_search 저장:fn_save 삭제:fn_delete");

	// 기본  + , - 7일 
	gfn_get_date('TODAY',-7,'YYYYMMDD');
	S_ST_DT.Text = GDS_DATE_TYPE.GetColumn(0,'DATE_VAL');

	gfn_get_date('TODAY',7,'YYYYMMDD');
	S_ED_DT.Text = GDS_DATE_TYPE.GetColumn(0,'DATE_VAL');

	fn_search();


}


