//*------------------------------------------------------------------------------------------------
//* 함수설명     : 내근업무 정보 추가 함수 
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function fn_insert()
{

	var bValidate = true;
	var pMessage = "";
	var cMessage = "";
	var isSave = false;

	// 필터 
	DS_AFF_PRSYES_LST.FireEvent = false;		
	DS_AFF_PRSYES_LST.UnFilter();
	DS_AFF_PRSYES_LST.Filter("FLAG !='N'");

	DS_AFFAIRPRSNOTE_LST.FireEvent = false;		
	DS_AFFAIRPRSNOTE_LST.UnFilter();
	DS_AFFAIRPRSNOTE_LST.Filter("FLAG !='N'");	

	// 정상일경우 처리
	if(bValidate == true)
	{
		// 저장 메시지는 뺀다.
		if(1==1)
		{
			// 새로운 DataSet에 담아서 보내야됨
			// 아니면 변경된 모든것들을 보내버리므로 안됨.
			ds_affairprs_temp.ClearData();
			ds_affairprs_temp.CopyF("DS_AFF_PRSYES_LST");

			ds_dtl_temp.ClearData();
			ds_dtl_temp.CopyF("DS_AFF_PRSYES_LST");

			ds_affairprsnote_temp.ClearData();
			ds_affairprsnote_temp.CopyF("DS_AFFAIRPRSNOTE_LST");


			svcID			= "ACAA02T01.insertAffairPrsLst";
			url   			= "service::common/select_mi.do";
			inDatasets  	= "ac.ACAffairDAO.saveAffairPrsNoteLst=ds_affairprsnote_temp:A"
							+ " ac.ACAffairDAO.saveAffairPrsLst=ds_affairprs_temp:A";

			outDatasets 	= "";
			argument    	= " _sqlName=ac.ACAffairDAO.saveAffairPrsNoteLst"
							+ ",ac.ACAffairDAO.saveAffairPrsLst"	
							;
			callbackFunc 	= "gfn_callback";	


			gfn_showProgressWindow(true);
			gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	  
		}

	}

	DS_AFF_PRSYES_LST.UnFilter();
	DS_AFF_PRSYES_LST.Filter("flag != 'D'");
	DS_AFF_PRSYES_LST.FireEvent = true;	

	DS_AFFAIRPRSNOTE_LST.UnFilter();
	DS_AFFAIRPRSNOTE_LST.Filter("flag != 'D'");
	DS_AFFAIRPRSNOTE_LST.FireEvent = true;	

}

//*------------------------------------------------------------------------------------------------
//* 함수설명     : 내근업무 정보 저장 함수 
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function fn_save()
{
	curYesRow = DS_AFF_PRSYES_TREE.currow;

	var bValidate = true;
	var pMessage = "";
	var cMessage = "";
	var isSave = false;

	// 필터 
	DS_AFF_PRSYES_LST.FireEvent = false;		
	DS_AFF_PRSYES_LST.UnFilter();
	DS_AFF_PRSYES_LST.Filter("FLAG !='N'");

	DS_AFFAIRPRSNOTE_LST.FireEvent = false;		
	DS_AFFAIRPRSNOTE_LST.UnFilter();
	DS_AFFAIRPRSNOTE_LST.Filter("FLAG !='N'");	


	if(DS_AFF_PRSYES_LST.rowcount > 0)
	{
		// 필수체크
		for(var i=0;i<DS_AFF_PRSYES_LST.rowcount;i++)
		{
			cMessage = ""; 

			if(bValidate == false)
			{
				pMessage += "\n ["+i+"] " + cMessage;
			}
		}
	}

	if(bValidate == false)
	{
		gfn_popup_message("modal", "error", cMessage, "확인");
	}
	else
	{
		isSave = true;
	}	

	// 정상일경우 처리
	if(bValidate == true)
	{
		// 저장 메시지는 뺀다.
		if(1==1)
		{
			// 새로운 DataSet에 담아서 보내야됨
			// 아니면 변경된 모든것들을 보내버리므로 안됨.
			ds_affairprs_temp.ClearData();
			ds_affairprs_temp.CopyF("DS_AFF_PRSYES_LST");

			ds_dtl_temp.ClearData();
			ds_dtl_temp.CopyF("DS_AFF_PRSYES_LST");

			ds_affairprsnote_temp.ClearData();
			ds_affairprsnote_temp.CopyF("DS_AFFAIRPRSNOTE_LST");


			svcID			= "ACAA02T01.saveAffairPrsLst";
			url   			= "service::common/select_mi.do";
			inDatasets  	= "ac.ACAffairDAO.saveAffairPrsNoteLst=ds_affairprsnote_temp:A"
							+ " ac.ACAffairDAO.saveAffairPrsLst=ds_affairprs_temp:A";

			outDatasets 	= "";
			argument    	= " _sqlName=ac.ACAffairDAO.saveAffairPrsNoteLst"
							+ ",ac.ACAffairDAO.saveAffairPrsLst"	
							;
			callbackFunc 	= "gfn_callback";	


			gfn_showProgressWindow(true);
			gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	  
		}

	}

	DS_AFF_PRSYES_LST.UnFilter();
	DS_AFF_PRSYES_LST.Filter("flag != 'D'");
	DS_AFF_PRSYES_LST.FireEvent = true;	

	DS_AFFAIRPRSNOTE_LST.UnFilter();
	DS_AFFAIRPRSNOTE_LST.Filter("flag != 'D'");
	DS_AFFAIRPRSNOTE_LST.FireEvent = true;	

	DS_AFF_PRSYES_TREE.row = curYesRow;

}


//*------------------------------------------------------------------------------------------------
//* 함수설명     : 1.4 삭제 함수 
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function fn_delete()
{
	var bValidate = true;
	var pMessage = "";
	var cMessage = "";

	// 선택된 접수현황 삭제 
	if(length(DS_AFF_PRSYES_LST.GetColumn(DS_AFF_PRSYES_LST.currow,"STS_SEQ")) == 0)
	{
		gfn_popup_message("modal", "error", "삭제할 접수현황이 없습니다.", "확인");
		bValidate = false;	
		return;
	}

	// 정상일경우 처리
	if(bValidate == true)
	{
		//삭제 
		if(gfn_popup_message("modal", "confirm", "선택된 접수현황을 삭제하시겠습니까? 삭제 후 복원이 불가능 합니다.", "확인")) 
		{

			svcID			= "ACAA02T01.deleteAffairPrsLst";
			url   			= "service::common/select_mi.do";
			inDatasets  	= "";
			outDatasets 	= "";
			argument    	= " _sqlName=ac.ACAffairDAO.deleteAffairPrsLst"
							+ " ROW_ID=" + quote(DS_AFF_PRSYES_LST.GetColumn(DS_AFF_PRSYES_LST.currow,"ROW_ID"))
							;			
			callbackFunc 	= "gfn_callback";	


			gfn_showProgressWindow(true);
			gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	 
		}

	}
}



//*------------------------------------------------------------------------------------------------
//* 함수설명     : 1.5 Transaction 호출후 결과값 확인 (gfn_callback에서 fn_callback을 호출한다.)
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function fn_callback(svcID, errorCode, errorMsg)
{

//	trace('call mst/dtl/note='+curNotRow+'/'+curYesRow+'/'+curNoteRow);

	switch(svcID) 
	{		
		// 접수대상 조회
		//  한건만 있으면 한건조회 
		case "ACAA02T01.selectAffPrsNotTree" :

			if(DS_AFF_PRSNOT_TREE.rowcount == 1)
			{
				fn_search_not_one(DS_AFF_PRSNOT_TREE.GetColumn(0,"BRCH_ID"), 
					DS_AFF_PRSNOT_TREE.GetColumn(0,"AFF_SEQ"),
					DS_AFF_PRSNOT_TREE.GetColumn(0,"DTL_SEQ"),
					DS_AFF_PRSNOT_TREE.GetColumn(0,"BND_SEQ")
				);

			}


		break;	

		// 접수대상 한건
		case "ACAA02T01.selectAffPrsNotOne" :



		break;	





		// 접수목록 조회
		case "ACAA02T01.selectAffPrsYesLst" :
			// 접수등록 필터링  
			//fn_prsyes_filter(curYesRow);			
		break;	


		// 접수목록 한건		
		case "ACAA02T01.selectAffPrsYesOne" :
			fn_search_yes();		
		break;	




		// 추가
		case "ACAA02T01.insertAffairPrsLst" :


			// 한건조회
			fn_search_yes();			

		break;	





		// 수정
		case "ACAA02T01.saveAffairPrsLst" :


			// 한건조회
			fn_search_yes_one(DS_AFF_PRSYES_TREE.GetColumn(DS_AFF_PRSYES_TREE.currow,"BRCH_ID"), 
								DS_AFF_PRSYES_TREE.GetColumn(DS_AFF_PRSYES_TREE.currow,"AFF_SEQ"),
							DS_AFF_PRSYES_TREE.GetColumn(DS_AFF_PRSYES_TREE.currow,"DTL_SEQ"),
							DS_AFF_PRSYES_TREE.GetColumn(DS_AFF_PRSYES_TREE.currow,"STS_SEQ")
						  );			

			break;	





		// 삭제
		case "ACAA02T01.deleteAffairPrsLst" :
			gfn_setMsg("내근업무 삭제 완료.");

			// 데이터셋 지우고 다시 조회
			DS_AFF_PRSYES_LST.ClearData();
			DS_AFFAIRPRSNOTE_LST.ClearData();

			fn_search_yes();		
		break;	

		default:
	}

	/*
	if(curNotRow != null) 
	{
		DS_AFF_PRSNOT_LST.row = curNotRow;
	}
	*/

	if(curYesRow != null) 
	{
		DS_AFF_PRSYES_TREE.row = curYesRow;
	}


	// 기본정보 수정불가 
	btn_mst_ok_n_OnClick();
	btn_mst_ok_y_OnClick();

}



/**************************************************************************************************
  * 사용자 정의  Function 처리 부분
***************************************************************************************************/
//*------------------------------------------------------------------------------------------------
//* 함수설명     : DTL 필터링 NO
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//*-----------------------------------------------------------------------------------------------
