//*------------------------------------------------------------------------------------------------
//* 함수설명     : 행추가
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function fn_add_dtl(isInsert)
{
	var vRow;
	var cMessage;
	var bValidate = true;

	// 기본정보가 선택되지 않으면 경고 
	if(length(DS_AFF_PRSNOT_LST.GetColumn(DS_AFF_PRSNOT_LST.curRow,"AFF_SEQ")) == 0)
	{
		gfn_popup_message("modal", "error", "먼저 접수대상을 선택 해야 합니다. ", "확인");
		return;
	}

	cMessage = ""; 

	if(length(DS_AFF_PRSNOT_LST.GetColumn(DS_AFF_PRSNOT_LST.curRow, "ACP_MTD")) == 0)
	{
		bValidate = false;
		cMessage += "접수방법누락 ";
	}

	if(gfn_isNullDate(DS_AFF_PRSNOT_LST.GetColumn(DS_AFF_PRSNOT_LST.curRow, "ACP_DT")) == true)
	{
		bValidate = false;
		cMessage += "접수일자 누락 ";
	}			

	if(bValidate == false)
	{
		gfn_popup_message("modal", "error", cMessage, "확인");
		return;
	}

	if(gfn_popup_message("modal", "confirm", "접수대상을 등록 하시겠습니까?", "확인") == false) 
	{
		return;
	}	

	if(isInsert == true)
	{
		vRow = DS_AFF_PRSYES_LST.InsertRow(DS_AFF_PRSYES_LST.currow+1);
	}
	else
	{
		vRow = DS_AFF_PRSYES_LST.AddRow();
	}


//	trace('aft vRow/mst/dtl/note='+vRow+'/'+curNotRow+'/'+curYesRow+'/'+curNoteRow);


//	curYesRow = vRow;

	// 기초값
	DS_AFF_PRSYES_LST.SetColumn(vRow,"FLAG","I");	// 추가 FLAG 넣기
	DS_AFF_PRSYES_LST.SetColumn(vRow,"ROW_ID","");	// ROW_ID(조회후 셋팅됨)
	DS_AFF_PRSYES_LST.SetColumn(vRow,"OPERATOR", GDS_USER_INFO.GetColumn(0, "USR_ID"));// 등록자, 수정자 넣기
	DS_AFF_PRSYES_LST.SetColumn(vRow,"CHK","1");	// 선택

    DS_AFF_PRSYES_LST.SetColumn(vRow,"BRCH_ID", DS_AFF_PRSNOT_LST.GetColumn(DS_AFF_PRSNOT_LST.curRow, "BRCH_ID"));// 
    DS_AFF_PRSYES_LST.SetColumn(vRow,"AFF_SEQ", DS_AFF_PRSNOT_LST.GetColumn(DS_AFF_PRSNOT_LST.curRow, "AFF_SEQ"));// 

    DS_AFF_PRSYES_LST.SetColumn(vRow,"DTL_SEQ", DS_AFF_PRSNOT_LST.GetColumn(DS_AFF_PRSNOT_LST.curRow, "DTL_SEQ"));// 
    DS_AFF_PRSYES_LST.SetColumn(vRow,"STS_SEQ", DS_AFF_PRSNOT_LST.GetColumn(DS_AFF_PRSNOT_LST.curRow, "STS_SEQ"));// 
    DS_AFF_PRSYES_LST.SetColumn(vRow,"ACP_MTD", DS_AFF_PRSNOT_LST.GetColumn(DS_AFF_PRSNOT_LST.curRow, "ACP_MTD"));// 
    DS_AFF_PRSYES_LST.SetColumn(vRow,"ACP_DT", DS_AFF_PRSNOT_LST.GetColumn(DS_AFF_PRSNOT_LST.curRow, "ACP_DT"));// 
    DS_AFF_PRSYES_LST.SetColumn(vRow,"CLT_NM", DS_AFF_PRSNOT_LST.GetColumn(DS_AFF_PRSNOT_LST.curRow, "CLT_NM"));// 
    DS_AFF_PRSYES_LST.SetColumn(vRow,"CLT_JMN", DS_AFF_PRSNOT_LST.GetColumn(DS_AFF_PRSNOT_LST.curRow, "CLT_JMN"));// 
    DS_AFF_PRSYES_LST.SetColumn(vRow,"PRS_STEP", DS_AFF_PRSNOT_LST.GetColumn(DS_AFF_PRSNOT_LST.curRow, "PRS_STEP"));// 
    DS_AFF_PRSYES_LST.SetColumn(vRow,"BND_SEQ", DS_AFF_PRSNOT_LST.GetColumn(DS_AFF_PRSNOT_LST.curRow, "BND_SEQ"));// 
    DS_AFF_PRSYES_LST.SetColumn(vRow,"BND_NM", DS_AFF_PRSNOT_LST.GetColumn(DS_AFF_PRSNOT_LST.curRow, "BND_NM"));// 
    DS_AFF_PRSYES_LST.SetColumn(vRow,"SPT_SEQ", DS_AFF_PRSNOT_LST.GetColumn(DS_AFF_PRSNOT_LST.curRow, "SPT_SEQ"));// 
    DS_AFF_PRSYES_LST.SetColumn(vRow,"SPT_NM", DS_AFF_PRSNOT_LST.GetColumn(DS_AFF_PRSNOT_LST.curRow, "SPT_NM"));// 
    DS_AFF_PRSYES_LST.SetColumn(vRow,"BND_TEL", DS_AFF_PRSNOT_LST.GetColumn(DS_AFF_PRSNOT_LST.curRow, "BND_TEL"));// 
    DS_AFF_PRSYES_LST.SetColumn(vRow,"BND_FAX", DS_AFF_PRSNOT_LST.GetColumn(DS_AFF_PRSNOT_LST.curRow, "BND_FAX"));// 
    DS_AFF_PRSYES_LST.SetColumn(vRow,"BND_ZIP", DS_AFF_PRSNOT_LST.GetColumn(DS_AFF_PRSNOT_LST.curRow, "BND_ZIP"));// 
    DS_AFF_PRSYES_LST.SetColumn(vRow,"BND_ADDR", DS_AFF_PRSNOT_LST.GetColumn(DS_AFF_PRSNOT_LST.curRow, "BND_ADDR"));// 
    DS_AFF_PRSYES_LST.SetColumn(vRow,"CHRG_AMT", DS_AFF_PRSNOT_LST.GetColumn(DS_AFF_PRSNOT_LST.curRow, "CHRG_AMT"));// 
        DS_AFF_PRSYES_LST.SetColumn(vRow,"PRS_STS", '200');// 발급진행  
    DS_AFF_PRSYES_LST.SetColumn(vRow,"PRS_NOTE", DS_AFF_PRSNOT_LST.GetColumn(DS_AFF_PRSNOT_LST.curRow, "PRS_NOTE"));// 
    DS_AFF_PRSYES_LST.SetColumn(vRow,"REGS_NO", DS_AFF_PRSNOT_LST.GetColumn(DS_AFF_PRSNOT_LST.curRow, "REGS_NO"));// 
    DS_AFF_PRSYES_LST.SetColumn(vRow,"PRS_CHG_USR", DS_AFF_PRSNOT_LST.GetColumn(DS_AFF_PRSNOT_LST.curRow, "PRS_CHG_USR"));// 
	DS_AFF_PRSYES_LST.SetColumn(vRow,"ACP_PLC_GBN", DS_AFF_PRSNOT_LST.GetColumn(DS_AFF_PRSNOT_LST.curRow, "ACP_PLC_GBN"));// 


 	DS_AFF_PRSYES_TREE.row = curYesRow;



	// 바로 저장 
	fn_insert();


}



