function btn_add_note_OnClick(obj)
{
	var vRow;

	// 기본정보가 선택되지 않으면 경고 
	if(length(DS_AFF_PRSYES_LST.GetColumn(DS_AFF_PRSYES_LST.curRow,"AFF_SEQ")) == 0)
	{
		gfn_popup_message("modal", "error", "먼저 접수진행내역을 선택 해야 합니다. ", "확인");
		return;
	}

	vRow = DS_AFFAIRPRSNOTE_LST.AddRow();


	// 기초값
	DS_AFFAIRPRSNOTE_LST.SetColumn(vRow,"FLAG","I");	// 추가 FLAG 넣기
	DS_AFFAIRPRSNOTE_LST.SetColumn(vRow,"ROW_ID","");	// ROW_ID(조회후 셋팅됨)
	DS_AFFAIRPRSNOTE_LST.SetColumn(vRow,"OPERATOR", GDS_USER_INFO.GetColumn(0, "USR_ID"));// 등록자, 수정자 넣기
	DS_AFFAIRPRSNOTE_LST.SetColumn(vRow,"CHK","1");	// 선택

    DS_AFFAIRPRSNOTE_LST.SetColumn(vRow,"BRCH_ID", DS_AFF_PRSYES_LST.GetColumn(DS_AFF_PRSYES_LST.curRow, "BRCH_ID"));// 
    DS_AFFAIRPRSNOTE_LST.SetColumn(vRow,"AFF_SEQ", DS_AFF_PRSYES_LST.GetColumn(DS_AFF_PRSYES_LST.curRow, "AFF_SEQ"));// 
    DS_AFFAIRPRSNOTE_LST.SetColumn(vRow,"DTL_SEQ", DS_AFF_PRSYES_LST.GetColumn(DS_AFF_PRSYES_LST.curRow, "DTL_SEQ"));// 
    DS_AFFAIRPRSNOTE_LST.SetColumn(vRow,"STS_SEQ", DS_AFF_PRSYES_LST.GetColumn(DS_AFF_PRSYES_LST.curRow, "STS_SEQ"));// 
    DS_AFFAIRPRSNOTE_LST.SetColumn(vRow,"PRS_DT", gfnGetToday(''));// 오늘
    DS_AFFAIRPRSNOTE_LST.SetColumn(vRow,"USR_ID", GDS_USER_INFO.GetColumn(0, "USR_ID"));// 
    DS_AFFAIRPRSNOTE_LST.SetColumn(vRow,"USR_NM", GDS_USER_INFO.GetColumn(0, "USR_NM"));// 

    // 저장은 행추가 후 저장 나머지는 데이터셋 변경시 저장 
    fn_save();

}



//*------------------------------------------------------------------------------------------------
//* 함수설명     : 행삭제 클릭시  실행 스크립트
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function btn_del_note_OnClick(obj)
{

	// 기본정보가 선택되지 않으면 경고 
	if(DS_AFFAIRPRSNOTE_LST.currow < 0)
	{
		gfn_popup_message("modal", "error", "삭제할 행이 없습니다. ", "확인");
		return;
	}

	//  확인 두번 하기 
	if(gfn_popup_message("modal", "confirm", "선택된 행을 삭제 하시겠습니까?", "확인") == false) 
	{
		return;
	}

	DS_AFFAIRPRSNOTE_LST.FireEvent = false;



	// 추가된것은 DB저장전이므로 그냥 DataSet에서 삭제
	if(DS_AFFAIRPRSNOTE_LST.GetColumn(DS_AFFAIRPRSNOTE_LST.currow,"FLAG") == "I")
	{
		DS_AFFAIRPRSNOTE_LST.DeleteRow(DS_AFFAIRPRSNOTE_LST.currow);
	}
	// 아닌것은 실제로 DB에 삭제해야되므로 flag를 D로 두고 마지막에 filter한다.
	else
	{
		DS_AFFAIRPRSNOTE_LST.SetColumn(DS_AFFAIRPRSNOTE_LST.currow,"FLAG", "D");
	}

	DS_AFFAIRPRSNOTE_LST.UnFilter();
	DS_AFFAIRPRSNOTE_LST.Filter("flag != 'D'");	

	DS_AFFAIRPRSNOTE_LST.FireEvent = true;

}




