function btn_del_spt_OnClick(obj)
{
	// 채권사 지점삭제
	if(gfn_popup_message("modal", "confirm", "선택된 지점을 삭제하시겠습니까? ", "확인")) 
	{
		DS_AFF_PRSNOT_LST.SetColumn(DS_AFF_PRSNOT_LST.currow,"SPT_SEQ", "");// 
		DS_AFF_PRSNOT_LST.SetColumn(DS_AFF_PRSNOT_LST.currow,"SPT_NM", "");// 	
	}
}


//*------------------------------------------------------------------------------------------------
//* 함수설명     : DataSet 변경시 자동저장
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function DS_AFF_PRSNOT_LST_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	// flag가 'N' 이면 'U' 로 바꾼다.
	if(obj.GetColumn(nRow, "FLAG") == "N" )
	{
		obj.SetColumn(nRow, "FLAG", "U");	// 수정했다는 표시 
	}
}



function DS_AFF_PRSYES_LST_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	// flag가 'N' 이면 'U' 로 바꾼다.
	if(obj.GetColumn(nRow, "FLAG") == "N" )
	{
		obj.SetColumn(nRow, "FLAG", "U");	// 수정했다는 표시 

		// 자동저장
		fn_save();
	}
}

