//* ÇÔ¼ö¼³¸í     : ¼ö¼ö·áÀÏ°èÇ¥ Á¤º¸ ÀúÀå ÇÔ¼ö 
//* ÇÔ¼öÀ¯Çü     : 
//* parameter    : 
//* return value : ¾øÀ½
//* ºñ°í         :
//*-----------------------------------------------------------------------------------------------
function fn_save()
{

	var bValidate = true;
	var pMessage = "";
	var cMessage = "";
	var isSave = false;

	// ÇÊÅÍ 
	DS_AFFAIR_YES_LST.FireEvent = false;		
	DS_AFFAIR_YES_LST.UnFilter();
	DS_AFFAIR_YES_LST.Filter("FLAG !='N'");

	if(DS_AFFAIR_YES_LST.rowcount > 0)
	{
		// ÇÊ¼öÃ¼Å©
		for(var i=0;i<DS_AFFAIR_YES_LST.rowcount;i++)
		{
			cMessage = ""; 

			if(bValidate == false)
			{
				pMessage += "\n ["+i+"] " + cMessage;
			}
		}
	}

	if(bValidate == false)
	{
		gfn_popup_message("modal", "error", cMessage, "È®ÀÎ");
	}
	else
	{
		isSave = true;
	}	

	// Á¤»óÀÏ°æ¿ì Ã³¸®
	if(bValidate == true)
	{
		// ÀúÀå ¸Þ½ÃÁö´Â »«´Ù.
		if(1==1)
		{
			// »õ·Î¿î DataSet¿¡ ´ã¾Æ¼­ º¸³»¾ßµÊ
			// ¾Æ´Ï¸é º¯°æµÈ ¸ðµç°ÍµéÀ» º¸³»¹ö¸®¹Ç·Î ¾ÈµÊ.
			ds_affairchrg_temp.ClearData();
			ds_affairchrg_temp.CopyF("DS_AFFAIR_YES_LST");

			svcID			= "ACAA09T01.saveAffairChrgLst";
			url   			= "service::common/select_mi.do";
			inDatasets  	= "ac.ACAffairDAO.saveAffairChrgLst=ds_affairchrg_temp:A";

			outDatasets 	= "";
			argument    	= " _sqlName=ac.ACAffairDAO.saveAffairChrgLst"
							;
			callbackFunc 	= "gfn_callback";	


			gfn_showProgressWindow(true);
			gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	  
		}

	}

	DS_AFFAIR_YES_LST.UnFilter();
	DS_AFFAIR_YES_LST.Filter("flag != 'D'");
	DS_AFFAIR_YES_LST.FireEvent = true;	


}


//*------------------------------------------------------------------------------------------------
//* ÇÔ¼ö¼³¸í     : 1.4 »èÁ¦ ÇÔ¼ö 
//* ÇÔ¼öÀ¯Çü     : 
//* parameter    : 
//* return value : ¾øÀ½
//* ºñ°í         :
//*-----------------------------------------------------------------------------------------------
function fn_delete()
{
	var bValidate = true;
	var pMessage = "";
	var cMessage = "";

	// ¼±ÅÃµÈ Á¢¼öÇöÈ² »èÁ¦ 
	if(length(DS_AFFAIR_YES_LST.GetColumn(DS_AFFAIR_YES_LST.currow,"ROW_ID")) == 0)
	{
		gfn_popup_message("modal", "error", "Ãë¼ÒÇÒ ¼ö¼ö·áÀÏ°èÇ¥°¡ ¾ø½À´Ï´Ù.", "È®ÀÎ");
		bValidate = false;	
		return;
	}

	// Á¤»óÀÏ°æ¿ì Ã³¸®
	if(bValidate == true)
	{
		//»èÁ¦ 
		if(gfn_popup_message("modal", "confirm", "¼±ÅÃµÈ ¼ö¼ö·áÀÏ°èÇ¥¸¦ Ãë¼ÒÇÏ½Ã°Ú½À´Ï±î? Ãë¼Ò ÈÄ º¹¿øÀÌ ºÒ°¡´É ÇÕ´Ï´Ù.", "È®ÀÎ")) 
		{

			svcID			= "ACAA09T01.deleteAffairChrgLst";
			url   			= "service::common/select_mi.do";
			inDatasets  	= "";
			outDatasets 	= "";
			argument    	= " _sqlName=ac.ACAffairDAO.deleteAffairChrgLst"
							+ " ROW_ID=" + quote(DS_AFFAIR_YES_LST.GetColumn(DS_AFFAIR_YES_LST.currow,"ROW_ID"))
							;			
			callbackFunc 	= "gfn_callback";	


			gfn_showProgressWindow(true);
			gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	 
		}

	}
}



//*------------------------------------------------------------------------------------------------
//* ÇÔ¼ö¼³¸í     : 1.5 Transaction È£ÃâÈÄ °á°ú°ª È®ÀÎ (gfn_callback¿¡¼­ fn_callbackÀ» È£ÃâÇÑ´Ù.)
//* ÇÔ¼öÀ¯Çü     : 
//* parameter    : 
//* return value : ¾øÀ½
//* ºñ°í         :
//*-----------------------------------------------------------------------------------------------
function fn_callback(svcID, errorCode, errorMsg)
{

//	trace('call mst/dtl/note='+curNotRow+'/'+curYesRow+'/'+curNoteRow);

	switch(svcID) 
	{		
		// µî·Ï´ë»ó Á¶È¸
		case "ACAA09T01.selectAffChrgNotLst" :
			// ¸¶°¨/¹Ì¸¶°¨ ÇÊÅÍ¸µ  
			fn_prsnot_filter();
		break;	

		// µî·Ï¸ñ·ÏÁ¶È¸
		case "ACAA09T01.selectAffChrgYesLst" :

		break;	

		// ÀúÀå
		case "ACAA09T01.saveAffairChrgLst" :
			gfn_setMsg("¼ö¼ö·áÀÏ°èÇ¥ ÀúÀå ¿Ï·á.");
			fn_search();			
			break;			

		// »èÁ¦
		case "ACAA09T01.deleteAffairChrgLst" :
			gfn_setMsg("¼ö¼ö·áÀÏ°èÇ¥ Ãë¼Ò ¿Ï·á.");
			fn_search();		
		break;	

		default:
	}


}



/**************************************************************************************************
  * »ç¿ëÀÚ Á¤ÀÇ  Function Ã³¸® ºÎºÐ

***************************************************************************************************/

//*------------------------------------------------------------------------------------------------
//* ÇÔ¼ö¼³¸í     : Ãß°¡
//* ÇÔ¼öÀ¯Çü     : 
//* parameter    : 
//* return value : ¾øÀ½
//* ºñ°í: dtl_seq »èÁ¦ÇÔ (¿Ö³Ä¸é »ç°Çµî·Ï¿¡¼­ Ã¤±Ç»ç°¡ Áßº¹ Ã¼Å©ÇÏ¸é¼­ dtl_seq°¡ ÀÇ¹Ì°¡ ¾ø¾îÁü)
//*-----------------------------------------------------------------------------------------------
function fn_add_bnd(BRCH_ID, AFF_SEQ, DTL_SEQ, PRS_STEP, BND_SEQ)
{
	//trace('brch_id/aff_seq/dtl_seq/prs_step/bnd_seq='+brch_id+'/'+aff_seq+'/'+dtl_seq+'/'+prs_step+'/'+bnd_seq);


	if(gfn_isNullDate(EDT_PAY_DT.Text) == true)
	{
		gfn_popup_message("modal", "error", "ÀÔ±ÝÀÏÀÚ¸¦ ÀÔ·ÂÇÏ¼¼¿ä.", "È®ÀÎ");
		S_PAY_DT.SetFocus();
		return;
	}

	if(gfn_popup_message("modal", "confirm", "¼ö¼ö·áÀÏ°èÇ¥¿¡ Ãß°¡ÇÏ½Ã°Ú½À´Ï±î?", "È®ÀÎ")) 
	{	
		// ÇØ´ç µ¥ÀÌÅÍ ÇÊÅÍ
		DS_AFFAIR_NOT_LST.FireEvent = false;		
		DS_AFFAIR_NOT_LST.UnFilter();
		DS_AFFAIR_NOT_LST.Filter("BRCH_ID='"+BRCH_ID+"' && AFF_SEQ='"+AFF_SEQ+"' && PRS_STEP='"+PRS_STEP+"' && BND_SEQ='"+BND_SEQ+"'");	

		// Ãß°¡¸ðµå·Î ¹Ù²Ù°í ³Ö´Â´Ù.
		DS_AFFAIR_NOT_LST.SetColumn(0, "FLAG","I");

		// Ç×¸ñ¼ÂÆÃ
		DS_AFFAIR_NOT_LST.SetColumn(0, "PAY_DT",EDT_PAY_DT.Text);		// Á¢¼öÀÏÀÚ

		ds_affairchrg_temp.ClearData();
		ds_affairchrg_temp.CopyF("DS_AFFAIR_NOT_LST");

		svcID			= "ACAA09T01.saveAffairChrgLst";
		url   			= "service::common/select_mi.do";
		inDatasets  	= "ac.ACAffairDAO.saveAffairChrgLst=ds_affairchrg_temp:A";
		outDatasets 	= "";
		argument    	= " _sqlName=ac.ACAffairDAO.saveAffairChrgLst"
						;
		callbackFunc 	= "gfn_callback";	


		gfn_showProgressWindow(true);
		gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	  	

		DS_AFFAIR_NOT_LST.UnFilter();
		DS_AFFAIR_NOT_LST.Filter("flag != 'D'");
		DS_AFFAIR_NOT_LST.FireEvent = true;		

	}


}





//*------------------------------------------------------------------------------------------------
//* ÇÔ¼ö¼³¸í     : DTL ÇÊÅÍ¸µ NO
//* ÇÔ¼öÀ¯Çü     : 
//* parameter    : 
//* return value : ¾øÀ½
//*-----------------------------------------------------------------------------------------------
function fn_prsnot_filter()
{

	DS_AFFAIR_NOT_LST.FireEvent = false;		

	// ¹Ì¸¶°¨ ÇÊÅÍ¸µ
	DS_AFFAIR_NOT_LST.UnFilter();
	DS_AFFAIR_NOT_LST.Filter("AFF_PRS_STS ='10'");	

	DS_AFFAIR_NOT_N_LST.ClearData();
	DS_AFFAIR_NOT_N_LST.CopyF("DS_AFFAIR_NOT_LST");	

	// ¸¶°¨ ÇÊÅÍ¸µ
	DS_AFFAIR_NOT_LST.UnFilter();
	DS_AFFAIR_NOT_LST.Filter("AFF_PRS_STS = '20' || AFF_PRS_STS = '25'");	

	DS_AFFAIR_NOT_Y_LST.ClearData();
	DS_AFFAIR_NOT_Y_LST.CopyF("DS_AFFAIR_NOT_LST");	

	DS_AFFAIR_NOT_LST.FireEvent = true;


}





