function DS_AFFAIR_NOT_LST_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	// flag°¡ 'N' ÀÌ¸é 'U' ·Î ¹Ù²Û´Ù.
	if(obj.GetColumn(nRow, "FLAG") == "N" )
	{
		obj.SetColumn(nRow, "FLAG", "U");	// ¼öÁ¤Çß´Ù´Â Ç¥½Ã 
	}
}



