//*------------------------------------------------------------------------
//* 함수설명     : 1.1 화면셋팅 함수 
//* 함수유형     :
//* parameter    :
//* return value : 없음
//* 비고         :
//*------------------------------------------------------------------------
function fn_initForm()
{

	//*------------------------------------------------------------------------
    //* 리사이즈 컨포넌트 정의
	//* H: 가로 리사이즈
	//* W: 세로 리사이즈
	//* A: 가로세로 리사이즈
	//* R: Resize
	//* M: Move
	//* Quick view로 테스트시 주석처리 후 테스트
    //*------------------------------------------------------------------------
	gfn_SetResize(this, "Shape_Out:H:R GRD_AFFAIR_NOT_LST_Y:W:R GRD_AFFAIR_YES_LST:W:R GRD_AFFAIRDELAY_LST:H:R btn_search_y:H:M");

	//gfn_SetResize(this, "GRD_BONDSPOT_LST:H:R GRD_AFFAIRDELAY_LST:H:R ");




	// 공통 명령어 조정
	gfn_set_cbutton("조회:fn_search 저장:fn_save 삭제:fn_delete");


	// 조회기간
	S_ST_DT.Text = gfnGetToday('');
	S_ED_DT.Text = gfnGetToday('');
	EDT_PAY_DT.Text = gfnGetToday('');



	fn_search();


}


//*------------------------------------------------------------------------------------------------
//* 함수설명     : 1.2 조회 함수 
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function fn_search()
{
	fn_search_not();
	fn_search_yes();

}


//*------------------------------------------------------------------------------------------------
//* 함수설명     : 1.2  접수등록대상 조회 함수 
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function fn_search_not()
{
	DS_AFFAIR_NOT_LST.ClearData();

	svcID			= "ACAA09T01.selectAffChrgNotLst";
	url   			= "service::common/select_mi.do";
	inDatasets  	= "";
	outDatasets 	= "DS_AFFAIR_NOT_LST=ac.ACAffairDAO.selectAffChrgNotLst"
							;	

	argument    	= " _sqlName=ac.ACAffairDAO.selectAffChrgNotLst"
							+ " S_BRCH_ID=" + quote(GDS_USER_INFO.BRCH_ID)
							+ " S_BND_NM="+quote(S_BND_NM_N.Text)
							+ " OPERATOR=" + quote(GDS_USER_INFO.USR_ID);							

	callbackFunc 	= "gfn_callback";

	gfn_showProgressWindow(true);
	gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	 	


}


//*------------------------------------------------------------------------------------------------
//* 함수설명     : 1.2  접수목록 조회 함수 
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function fn_search_yes()
{

	DS_AFFAIR_YES_LST.ClearData();

	svcID			= "ACAA09T01.selectAffChrgYesLst";
	url   			= "service::common/select_mi.do";
	inDatasets  	= "";
	outDatasets 	= "DS_AFFAIR_YES_LST=ac.ACAffairDAO.selectAffChrgYesLst"
							;	

	argument    	= " _sqlName=ac.ACAffairDAO.selectAffChrgYesLst"
							+ " S_BRCH_ID=" + quote(GDS_USER_INFO.BRCH_ID)
							+ " S_BND_NM="+quote(S_BND_NM_Y.Text)
							+ " S_ST_DT="+quote(S_ST_DT.Text)
							+ " S_ED_DT="+quote(S_ED_DT.Text)
							+ " OPERATOR=" + quote(GDS_USER_INFO.USR_ID);							

	callbackFunc 	= "gfn_callback";

	gfn_showProgressWindow(true);
	gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	 	


}



//*------------------------------------------------------------------------------------------------
//* 함수설명     : 수수료일계표 정보 저장 함수 
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function fn_save()
{

	var bValidate = true;
	var pMessage = "";
	var cMessage = "";
	var isSave = false;

	// 필터 
	DS_AFFAIR_YES_LST.FireEvent = false;		
	DS_AFFAIR_YES_LST.UnFilter();
	DS_AFFAIR_YES_LST.Filter("FLAG !='N'");

	if(DS_AFFAIR_YES_LST.rowcount > 0)
	{
		// 필수체크
		for(var i=0;i<DS_AFFAIR_YES_LST.rowcount;i++)
		{
			cMessage = ""; 

			if(bValidate == false)
			{
				pMessage += "\n ["+i+"] " + cMessage;
			}
		}
	}

	if(bValidate == false)
	{
		gfn_popup_message("modal", "error", cMessage, "확인");
	}
	else
	{
		isSave = true;
	}	

	// 정상일경우 처리
	if(bValidate == true)
	{
		// 저장 메시지는 뺀다.
		if(1==1)
		{
			// 새로운 DataSet에 담아서 보내야됨
			// 아니면 변경된 모든것들을 보내버리므로 안됨.
			ds_affairchrg_temp.ClearData();
			ds_affairchrg_temp.CopyF("DS_AFFAIR_YES_LST");

			svcID			= "ACAA09T01.saveAffairChrgLst";
			url   			= "service::common/select_mi.do";
			inDatasets  	= "ac.ACAffairDAO.saveAffairChrgLst=ds_affairchrg_temp:A";

			outDatasets 	= "";
			argument    	= " _sqlName=ac.ACAffairDAO.saveAffairChrgLst"
							;
			callbackFunc 	= "gfn_callback";	


			gfn_showProgressWindow(true);
			gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	  
		}

	}

	DS_AFFAIR_YES_LST.UnFilter();
	DS_AFFAIR_YES_LST.Filter("flag != 'D'");
	DS_AFFAIR_YES_LST.FireEvent = true;	


}


//*------------------------------------------------------------------------------------------------
//* 함수설명     : 1.4 삭제 함수 
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function fn_delete()
{
	var bValidate = true;
	var pMessage = "";
	var cMessage = "";

	// 선택된 접수현황 삭제 
	if(length(DS_AFFAIR_YES_LST.GetColumn(DS_AFFAIR_YES_LST.currow,"ROW_ID")) == 0)
	{
		gfn_popup_message("modal", "error", "취소할 수수료일계표가 없습니다.", "확인");
		bValidate = false;	
		return;
	}

	// 정상일경우 처리
	if(bValidate == true)
	{
		//삭제 
		if(gfn_popup_message("modal", "confirm", "선택된 수수료일계표를 취소하시겠습니까? 취소 후 복원이 불가능 합니다.", "확인")) 
		{

			svcID			= "ACAA09T01.deleteAffairChrgLst";
			url   			= "service::common/select_mi.do";
			inDatasets  	= "";
			outDatasets 	= "";
			argument    	= " _sqlName=ac.ACAffairDAO.deleteAffairChrgLst"
							+ " ROW_ID=" + quote(DS_AFFAIR_YES_LST.GetColumn(DS_AFFAIR_YES_LST.currow,"ROW_ID"))
							;			
			callbackFunc 	= "gfn_callback";	


			gfn_showProgressWindow(true);
			gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	 
		}

	}
}



//*------------------------------------------------------------------------------------------------
//* 함수설명     : 1.5 Transaction 호출후 결과값 확인 (gfn_callback에서 fn_callback을 호출한다.)
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function fn_callback(svcID, errorCode, errorMsg)
{

//	trace('call mst/dtl/note='+curNotRow+'/'+curYesRow+'/'+curNoteRow);

	switch(svcID) 
	{		
		// 등록대상 조회
		case "ACAA09T01.selectAffChrgNotLst" :
			// 마감/미마감 필터링  
			fn_prsnot_filter();
		break;	

		// 등록목록조회
		case "ACAA09T01.selectAffChrgYesLst" :

		break;	

		// 저장
		case "ACAA09T01.saveAffairChrgLst" :
			gfn_setMsg("수수료일계표 저장 완료.");
			fn_search();			
			break;			

		// 삭제
		case "ACAA09T01.deleteAffairChrgLst" :
			gfn_setMsg("수수료일계표 취소 완료.");
			fn_search();		
		break;	

		default:
	}


}



/**************************************************************************************************
  * 사용자 정의  Function 처리 부분

***************************************************************************************************/

//*------------------------------------------------------------------------------------------------
//* 함수설명     : 추가
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고: dtl_seq 삭제함 (왜냐면 사건등록에서 채권사가 중복 체크하면서 dtl_seq가 의미가 없어짐)
//*-----------------------------------------------------------------------------------------------
function fn_add_bnd(BRCH_ID, AFF_SEQ, DTL_SEQ, PRS_STEP, BND_SEQ)
{
	//trace('brch_id/aff_seq/dtl_seq/prs_step/bnd_seq='+brch_id+'/'+aff_seq+'/'+dtl_seq+'/'+prs_step+'/'+bnd_seq);


	if(gfn_isNullDate(EDT_PAY_DT.Text) == true)
	{
		gfn_popup_message("modal", "error", "입금일자를 입력하세요.", "확인");
		S_PAY_DT.SetFocus();
		return;
	}

	if(gfn_popup_message("modal", "confirm", "수수료일계표에 추가하시겠습니까?", "확인")) 
	{	
		// 해당 데이터 필터
		DS_AFFAIR_NOT_LST.FireEvent = false;		
		DS_AFFAIR_NOT_LST.UnFilter();
		DS_AFFAIR_NOT_LST.Filter("BRCH_ID='"+BRCH_ID+"' && AFF_SEQ='"+AFF_SEQ+"' && PRS_STEP='"+PRS_STEP+"' && BND_SEQ='"+BND_SEQ+"'");	

		// 추가모드로 바꾸고 넣는다.
		DS_AFFAIR_NOT_LST.SetColumn(0, "FLAG","I");

		// 항목셋팅
		DS_AFFAIR_NOT_LST.SetColumn(0, "PAY_DT",EDT_PAY_DT.Text);		// 접수일자

		ds_affairchrg_temp.ClearData();
		ds_affairchrg_temp.CopyF("DS_AFFAIR_NOT_LST");

		svcID			= "ACAA09T01.saveAffairChrgLst";
		url   			= "service::common/select_mi.do";
		inDatasets  	= "ac.ACAffairDAO.saveAffairChrgLst=ds_affairchrg_temp:A";
		outDatasets 	= "";
		argument    	= " _sqlName=ac.ACAffairDAO.saveAffairChrgLst"
						;
		callbackFunc 	= "gfn_callback";	


		gfn_showProgressWindow(true);
		gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	  	

		DS_AFFAIR_NOT_LST.UnFilter();
		DS_AFFAIR_NOT_LST.Filter("flag != 'D'");
		DS_AFFAIR_NOT_LST.FireEvent = true;		

	}


}





//*------------------------------------------------------------------------------------------------
//* 함수설명     : DTL 필터링 NO
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//*-----------------------------------------------------------------------------------------------
function fn_prsnot_filter()
{

	DS_AFFAIR_NOT_LST.FireEvent = false;		

	// 미마감 필터링
	DS_AFFAIR_NOT_LST.UnFilter();
	DS_AFFAIR_NOT_LST.Filter("AFF_PRS_STS ='10'");	

	DS_AFFAIR_NOT_N_LST.ClearData();
	DS_AFFAIR_NOT_N_LST.CopyF("DS_AFFAIR_NOT_LST");	

	// 마감 필터링
	DS_AFFAIR_NOT_LST.UnFilter();
	DS_AFFAIR_NOT_LST.Filter("AFF_PRS_STS = '20' || AFF_PRS_STS = '25'");	

	DS_AFFAIR_NOT_Y_LST.ClearData();
	DS_AFFAIR_NOT_Y_LST.CopyF("DS_AFFAIR_NOT_LST");	

	DS_AFFAIR_NOT_LST.FireEvent = true;


}





