					DS_AFFAIR_NOT_N_LST.GetColumn(nRow, "AFF_SEQ"), 
					DS_AFFAIR_NOT_N_LST.GetColumn(nRow, "DTL_SEQ"), 
					DS_AFFAIR_NOT_N_LST.GetColumn(nRow, "PRS_STEP"), 
					DS_AFFAIR_NOT_N_LST.GetColumn(nRow, "BND_SEQ")
				);
	}
}

