//*------------------------------------------------------------------------------------------------
//* 함수설명     : Grid 더블 클릭시 
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//*-----------------------------------------------------------------------------------------------
function GRD_BONDSPOT_LST_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	// 채권사 지점으로 넣기 
    DS_AFFAIR_NOT_LST.SetColumn(curNotRow,"SPT_SEQ", DS_BONDSPOT_LST.GetColumn(nRow, "SPT_SEQ"));// 
    DS_AFFAIR_NOT_LST.SetColumn(curNotRow,"SPT_NM", DS_BONDSPOT_LST.GetColumn(nRow, "SPT_NM"));// 

    // 접수방법 있으면 넣기
    if(length(DS_BONDSPOT_LST.GetColumn(nRow, "ACP_MTD")) != 0)
    {
		DS_AFFAIR_NOT_LST.SetColumn(curNotRow,"ACP_MTD", DS_BONDSPOT_LST.GetColumn(nRow, "ACP_MTD"));// 
	}

}




function GRD_AFFAIRPRSNOTE_LST_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	// 달력
	if(nCell == 3 )
	{
		gfn_PopupGridCalendar(obj, nRow, nCell);
	}
}


//*------------------------------------------------------------------------------------------------
//* 함수설명     : DataSet 변경시 자동저장
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function DS_AFFAIR_NOT_LST_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	// flag가 'N' 이면 'U' 로 바꾼다.
	if(obj.GetColumn(nRow, "FLAG") == "N" )
	{
		obj.SetColumn(nRow, "FLAG", "U");	// 수정했다는 표시 
	}
}



function DS_AFFAIR_YES_LST_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	// flag가 'N' 이면 'U' 로 바꾼다.
	if(obj.GetColumn(nRow, "FLAG") == "N" )
	{
		obj.SetColumn(nRow, "FLAG", "U");	// 수정했다는 표시 
	}

}




