//* ÇÔ¼ö¼³¸í     : °áÀç¿äÃ» 
//* ÇÔ¼öÀ¯Çü     : 
//* parameter    : 
//* return value : ¾øÀ½
//*-----------------------------------------------------------------------------------------------
function btn_sin_req_OnClick(obj)
{

	var bValidate = true;
	var pMessage = "";
	var cMessage = "";

	// ÇÊÅÍ 
	DS_TIEUPPAY_NOT_LST.FireEvent = false;		
	DS_TIEUPPAY_NOT_LST.UnFilter();
	DS_TIEUPPAY_NOT_LST.Filter("CHK !='0'");


	// ±Ý¾× Ã¼Å© 
	if(DS_TIEUPPAY_NOT_LST.rowcount > 0)
	{
		// ÇÊ¼öÃ¼Å©
		for(var i=0;i<DS_TIEUPPAY_NOT_LST.rowcount;i++)
		{

			cMessage = "";


			if(length(DS_TIEUPPAY_NOT_LST.GetColumn(i, "PAY_AMT")) == 0)
			{
				bValidate = false;
				cMessage += "±Ý¾× ´©¶ô ";
			}



			/*
			if(ToNumber(DS_TIEUPPAY_NOT_LST.GetColumn(i, "CHK_AMT")) < ToNumber(DS_TIEUPPAY_NOT_LST.GetColumn(i, "PAY_AMT")))
			{

				bValidate = false;
				cMessage += "ÀÔ±Ý¾×º¸´Ù °áÀç¿äÃ»¾×ÀÌ Å®´Ï´Ù. ";
			}
			*/


			if(bValidate == false)
			{
				pMessage += "\n ["+i+"] " + cMessage;
			}
		}

		// ÇÊ¼öÇ×¸ñ ´©¶ô 
		if(bValidate == false)
		{
			gfn_popup_message("modal", "error", pMessage, "È®ÀÎ");
		}
	}
	else
	{
		gfn_popup_message("modal", "error", "°áÀç¿äÃ»ÇÒ Ç×¸ñÀÌ ¾ø½À´Ï´Ù. Ç×¸ñÀ» ¸ÕÀú Ã¼Å©ÇÏ¼¼¿ä.", "È®ÀÎ");
		bValidate = false;		

	}

	// Á¤»óÀÏ°æ¿ì Ã³¸®
	if(bValidate == true)
	{
		// ÀúÀå 
		if(gfn_popup_message("modal", "confirm", "¼±ÅÃµÈ Ç×¸ñÀ»  °áÀç¿äÃ» Ã³¸®  ÇÏ½Ã°Ú½À´Ï±î?", "È®ÀÎ")) 
		{

			// »õ·Î¿î DataSet¿¡ ´ã¾Æ¼­ º¸³»¾ßµÊ
			// ¾Æ´Ï¸é º¯°æµÈ ¸ðµç°ÍµéÀ» º¸³»¹ö¸®¹Ç·Î ¾ÈµÊ.
			ds_not_lst.ClearData();
			ds_not_lst.CopyF("DS_TIEUPPAY_NOT_LST");

			svcID			= "ACAD02T01.saveTieupPayLst";
			url   			= "service::common/select_mi.do";
			inDatasets  	= "ac.ACAffairDAO.saveTieupPayLst=ds_not_lst:A";
			outDatasets 	= "";
