//* ÇÔ¼ö¼³¸í     : DataSet º¯°æ½Ã ÀÚµ¿ÀúÀå
//* ÇÔ¼öÀ¯Çü     : 
//* parameter    : 
//* return value : ¾øÀ½
//* ºñ°í         :
//*-----------------------------------------------------------------------------------------------
function DS_PAY_LST_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	// flag°¡ 'N' ÀÌ¸é 'U' ·Î ¹Ù²Û´Ù.
	if(obj.GetColumn(nRow, "FLAG") == "N" )
	{
		obj.SetColumn(nRow, "FLAG", "U");	// ¼öÁ¤Çß´Ù´Â Ç¥½Ã 
	}	
}



function DS_TIEUPPAY_NOT_LST_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	if(strColumnID == 'PAY_AMT')
	{

		// flag°¡ 'N' ÀÌ¸é 'U' ·Î ¹Ù²Û´Ù.
		if(obj.GetColumn(nRow, "FLAG") == "N" )
		{
			obj.SetColumn(nRow, "FLAG", "U");	// ¼öÁ¤Çß´Ù´Â Ç¥½Ã 
		}

		obj.SetColumn(nRow, "CHK", "1");	// Ã¼Å©


		// ¹Ì¼ö±Ý°ú ÀÔ±ÝÃ³¸®±ÝÀÌ Æ²¸®¸é °æ°íÃ¢ ÇÑ¹ø ¶ç¿ì±â 
		if(obj.GetColumn(nRow, "ASK_AMT") != obj.GetColumn(nRow, "PAY_AMT"))
		{
			gfn_popup_message("modal", "error", "¹Ì¼ö±Ý°ú ÀÔ±ÝÃ³¸®±ÝÀÌ Æ²¸³´Ï´Ù.", "È®ÀÎ");
		}

	}

	// ÇÕ°è±Ý¾× ³Ö±â
	var tmp_tub_pay = 0;

	for(var i=0;i<obj.rowcount;i++)
	{
		tmp_tub_pay += ToNumber(obj.GetColumn(i, "PAY_AMT"));
	}

	S_TUB_PAY.Text = tmp_tub_pay;




}


