//* 함수설명     : 1.1 화면셋팅 함수 
//* 함수유형     :
//* parameter    :
//* return value : 없음
//* 비고         :
//*------------------------------------------------------------------------
function fn_initForm()
{

	//*------------------------------------------------------------------------
    //* 리사이즈 컨포넌트 정의
	//* H: 가로 리사이즈
	//* W: 세로 리사이즈
	//* A: 가로세로 리사이즈
	//* R: Resize
	//* M: Move
	//* Quick view로 테스트시 주석처리 후 테스트
    //*------------------------------------------------------------------------
	gfn_SetResize(this, "Shape_Search:H:R GRD_TIEUPPAY_SIN_LST:A:R GRD_TIEUPPAY_YES_LST:W:R GRD_TIEUPPAY_YES_DTL:W:R btn_req_cancel:H:M btn_sin:H:M");


	// 처리현황(입금결재구분)
	//gfn_comCodeCombo(S_SIN_GBN, "AD15", "","전체");

	// 입금통장
	gfn_comCodeCombo(S_BANK_GBN, "AD16", "","전체");
	gfn_comCodeCombo(S_BANK_GBN_Y, "AD16", "","전체");


	// 공통 명령어 조정
	gfn_set_cbutton("조회:fn_search 입금확인불가목록:fn_no_pay 제휴사비용거래:fn_tub_pay");

	// 기본 기간은 오늘  
	gfn_get_date('TODAY',0,'YYYYMMDD');	

	S_PAY_ST_DT.Text = GDS_DATE_TYPE.GetColumn(0,'DATE_VAL');
	S_PAY_ED_DT.Text = GDS_DATE_TYPE.GetColumn(0,'DATE_VAL');
	S_ST_DT.Text = GDS_DATE_TYPE.GetColumn(0,'DATE_VAL');
	S_ED_DT.Text = GDS_DATE_TYPE.GetColumn(0,'DATE_VAL');

	S_PAY_ST_DT.Text = '20140101';
	S_ST_DT.Text = '20140101';

	fn_search();

}






//*------------------------------------------------------------------------------------------------
//* 함수설명     : 1.2 조회 함수 
//* 함수유형     : 
//* parameter    : 
