function fn_search()
{
	fn_pay_search();
	fn_payok_select();

}



//*------------------------------------------------------------------------------------------------
//* 함수설명     : 1.2 입금조회
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function fn_pay_search()
{
	curNotRow = DS_PAY_LST.currow;

	DS_PAY_LST.ClearData();


	svcID			= "ACAD02T01.selectPayLst";
	url   			= "service::common/select_mi.do";
	inDatasets  	= "";
	outDatasets 	= "DS_PAY_LST=ac.ACAffairDAO.selectPayLst"
					;		

	argument    	= " _sqlName=ac.ACAffairDAO.selectPayLst"
							+ " S_BRCH_ID=" + quote(GDS_USER_INFO.BRCH_ID)
							+ " S_PAY_NOTE="+quote(S_PAY_NOTE.Text)
							+ " S_PAY_ST_DT="+quote(S_PAY_ST_DT.Value)
							+ " S_PAY_ED_DT="+quote(S_PAY_ED_DT.Value)
							+ " S_BANK_GBN="+quote(S_BANK_GBN.Value)
							+ " S_SIN_GBN="+quote('10, 20') // 등록/결재중
							+ " S_PAY_ST_DT="+quote(S_PAY_ST_DT.Text)
							+ " S_PAY_ED_DT="+quote(S_PAY_ED_DT.Text)
							+ " OPERATOR=" + quote(GDS_USER_INFO.USR_ID);							

	callbackFunc 	= "gfn_callback";

	gfn_showProgressWindow(true);
	gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	 
}



//*------------------------------------------------------------------------------------------------
//* 함수설명     : 입금대상 조회
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//*-----------------------------------------------------------------------------------------------
function fn_payreq_select()
{
	// 검색제휴사 조회
	// 조회조건: 제휴사명칭, 제휴사담당자, 결제담당자, 예전적요관련 제휴사 => 비고
	DS_TIEUPPAY_NOT_LST.ClearData();
	DS_TIEUPPAY_SIN_LST.ClearData();
	DS_PAYMNG_INFO.ClearData();


	svcID			= "ACAD02T01.selectPayNotLst";
	url   			= "service::common/select_mi.do";
	inDatasets  	= "";
	outDatasets 	= " DS_TIEUPPAY_NOT_LST=ac.ACAffairDAO.selectPayNotLst"
					+ " DS_TIEUPPAY_SIN_LST=ac.ACAffairDAO.selectPaySinLst"	
					+ " DS_PAYMNG_INFO=ac.ACAffairDAO.selectTubPaySum"	
					;		

	argument    	= " _sqlName=ac.ACAffairDAO.selectPayNotLst"
							+ ",ac.ACAffairDAO.selectPaySinLst"
							+ ",ac.ACAffairDAO.selectTubPaySum"
							+ " S_BRCH_ID=" + quote(DS_PAY_LST.GetColumn(DS_PAY_LST.currow,"BRCH_ID"))
							+ " S_PAY_DT=" + quote(DS_PAY_LST.GetColumn(DS_PAY_LST.currow,"PAY_DT"))
							+ " S_PAY_SEQ=" + quote(DS_PAY_LST.GetColumn(DS_PAY_LST.currow,"PAY_SEQ"))
							+ " S_PAY_AMT=" + quote(DS_PAY_LST.GetColumn(DS_PAY_LST.currow,"PAY_AMT"))
							+ " S_PAY_NOTE="+quote(DS_PAY_LST.GetColumn(DS_PAY_LST.currow,"PAY_NOTE"))
							+ " OPERATOR=" + quote(GDS_USER_INFO.USR_ID);							

	callbackFunc 	= "gfn_callback";

	gfn_showProgressWindow(true);
	gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);



}

