//*------------------------------------------------------------------------------------------------
//* 함수설명     : 1.4 입금삭제 함수 
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function fn_pay_delete()
{
	var bValidate = true;
	var pMessage = "";
	var cMessage = "";

	// 선택된 접수현황 삭제 
	if(length(DS_PAY_LST.GetColumn(DS_PAY_LST.currow,"ROW_ID")) == 0)
	{
		gfn_popup_message("modal", "error", "삭제할 입금항목이 없습니다.", "확인");
		bValidate = false;	
		return;
	}

	// 등록이 아니면 삭제 불가 
	if(DS_PAY_LST.GetColumn(DS_PAY_LST.currow,"SIN_GBN") == '20' || DS_PAY_LST.GetColumn(DS_PAY_LST.currow,"SIN_GBN") == '30')
	{
		gfn_popup_message("modal", "error", "결재 진행상태가 결재중, 결재완료 일때는 삭제가 불가능 합니다.", "확인");
		return;
	}


	// 정상일경우 처리
	if(bValidate == true)
	{
		//삭제 
		if(gfn_popup_message("modal", "confirm", "선택된 입금내역을 삭제하시겠습니까? 삭제 후 복원이 불가능 합니다.", "확인")) 
		{

			svcID			= "ACAD02T01.deletePayLst";
			url   			= "service::common/select_mi.do";
			inDatasets  	= "";
			outDatasets 	= "";
			argument    	= " _sqlName=ac.ACAffairDAO.deletePayLst"
							+ " ROW_ID=" + quote(DS_PAY_LST.GetColumn(DS_PAY_LST.currow,"ROW_ID"))
							;			
			callbackFunc 	= "gfn_callback";	


			gfn_showProgressWindow(true);
			gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	 
		}

	}
}






//*------------------------------------------------------------------------------------------------
//* 함수설명     : 1.5 Transaction 호출후 결과값 확인 (gfn_callback에서 fn_callback을 호출한다.)
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function fn_callback(svcID, errorCode, errorMsg)
{

//	trace('call mst/dtl/note='+curNotRow+'/'+curYesRow+'/'+curNoteRow);

	switch(svcID) 
	{		

		// 입금조회
		case "ACAD02T01.selectPayLst" :

			// 미결재목록은 선택된거 유지함
			DS_PAY_LST.row = curNotRow;

			fn_payreq_select();

		break;			

		// 조회
		case "ACAD02T01.selectPayNotLst" :

		break;	

		// 미수전체조회
		case "ACAD02T01.selectPayNotLstAll" :

		break;

		// 결재완료조회
		case "ACAD02T01.selectPayYesLst" :

			// 결재완료목록은 선택된거 유지함
			DS_TIEUPPAY_YES_LST.row = curYesRow;

			fn_payok_dtl();

		break;	






		case "ACAD02T01.deletePayLst" :
			gfn_setMsg("입금내역 삭제 완료.");
			fn_search();			
			break;	


		// 입금확인 불가 
		case "ACAD02T01.savePayNot" :
			gfn_setMsg("입금확인불가 완료.");
			fn_search();
			break;			

		// 결재요청  
		case "ACAD02T01.saveTieupPayLst" :
			gfn_setMsg("결재요청 완료.");
			fn_search();
		break;	

		// 결재요청 취소
		case "ACAD02T01.deleteTieupPayLst" :
			gfn_setMsg("결재요청 취소 완료.");
			fn_search();
		break;			

		// 결재처리 
		case "ACAD02T01.closeTieupPayLst" :
			gfn_setMsg("결재처리 완료.");
			fn_search();
		break;		

		// 결재취소 
		case "ACAD02T01.cancelTieupPayLst" :
			gfn_setMsg("결재취소 완료.");
			fn_search();
		break;			


		default:
	}


}



