  * 사용자 정의  Function 처리 부분

***************************************************************************************************/


//*------------------------------------------------------------------------------------------------
//* 함수설명     :  입금확인불가 등록 함수 
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function fn_pay_not_add()
{
	var bValidate = true;
	var pMessage = "";
	var cMessage = "";

	// 선택된 입금항목 확인불가 처리  
	if(length(DS_PAY_LST.GetColumn(DS_PAY_LST.currow,"ROW_ID")) == 0)
	{
		gfn_popup_message("modal", "error", "입금확인 불가 항목이 없습니다. 먼저 저장 후 확인 불가 처리 하세요", "확인");
		bValidate = false;	
		return;
	}

	// 정상일경우 처리
	if(bValidate == true)
	{
		//삭제 
		if(gfn_popup_message("modal", "confirm", "선택된 항목을 입금확인불가 등록 하시겠습니까? ", "확인")) 
		{

			svcID			= "ACAD02T01.savePayNot";
			url   			= "service::common/select_mi.do";
			inDatasets  	= "";
			outDatasets 	= "";
			argument    	= " _sqlName=ac.ACAffairDAO.savePayNot"
							+ " ROW_ID=" + quote(DS_PAY_LST.GetColumn(DS_PAY_LST.currow,"ROW_ID"))
							+ " OPERATOR=" + quote(GDS_USER_INFO.USR_ID);							

			callbackFunc 	= "gfn_callback";	


			gfn_showProgressWindow(true);
			gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	 
		}

	}	
}


//*------------------------------------------------------------------------------------------------
//* 함수설명     : 결재요청 
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//*-----------------------------------------------------------------------------------------------
function btn_sin_req_OnClick(obj)
{

	var bValidate = true;
	var pMessage = "";
	var cMessage = "";

	// 필터 
	DS_TIEUPPAY_NOT_LST.FireEvent = false;		
	DS_TIEUPPAY_NOT_LST.UnFilter();
	DS_TIEUPPAY_NOT_LST.Filter("CHK !='0'");


	// 금액 체크 
	if(DS_TIEUPPAY_NOT_LST.rowcount > 0)
	{
		// 필수체크
		for(var i=0;i<DS_TIEUPPAY_NOT_LST.rowcount;i++)
		{

			cMessage = "";


			if(length(DS_TIEUPPAY_NOT_LST.GetColumn(i, "PAY_AMT")) == 0)
			{
				bValidate = false;
				cMessage += "금액 누락 ";
			}



			/*
			if(ToNumber(DS_TIEUPPAY_NOT_LST.GetColumn(i, "CHK_AMT")) < ToNumber(DS_TIEUPPAY_NOT_LST.GetColumn(i, "PAY_AMT")))
			{

				bValidate = false;
				cMessage += "입금액보다 결재요청액이 큽니다. ";
			}
			*/


			if(bValidate == false)
			{
				pMessage += "\n ["+i+"] " + cMessage;
			}
		}

		// 필수항목 누락 
		if(bValidate == false)
		{
			gfn_popup_message("modal", "error", pMessage, "확인");
		}
	}
	else
	{
		gfn_popup_message("modal", "error", "결재요청할 항목이 없습니다. 항목을 먼저 체크하세요.", "확인");
		bValidate = false;		

	}

	// 정상일경우 처리
	if(bValidate == true)
	{
		// 저장 
		if(gfn_popup_message("modal", "confirm", "선택된 항목을  결재요청 처리  하시겠습니까?", "확인")) 
		{

			// 새로운 DataSet에 담아서 보내야됨
			// 아니면 변경된 모든것들을 보내버리므로 안됨.
			ds_not_lst.ClearData();
			ds_not_lst.CopyF("DS_TIEUPPAY_NOT_LST");

			svcID			= "ACAD02T01.saveTieupPayLst";
			url   			= "service::common/select_mi.do";
			inDatasets  	= "ac.ACAffairDAO.saveTieupPayLst=ds_not_lst:A";
			outDatasets 	= "";
			argument    	= " _sqlName=ac.ACAffairDAO.saveTieupPayLst";

			callbackFunc 	= "gfn_callback";	


			gfn_showProgressWindow(true);
			gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	 
		}

	}

	DS_TIEUPPAY_NOT_LST.UnFilter();
	DS_TIEUPPAY_NOT_LST.FireEvent = true;	

}


//*------------------------------------------------------------------------------------------------
//* 함수설명     :  결재요청 취소 
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function fn_req_cancel()
{
	var bValidate = true;
	var pMessage = "";
	var cMessage = "";

	// 필터 
	DS_TIEUPPAY_SIN_LST.FireEvent = false;		
	DS_TIEUPPAY_SIN_LST.UnFilter();
	DS_TIEUPPAY_SIN_LST.Filter("CHK !='0'");


	if(DS_TIEUPPAY_SIN_LST.rowcount > 0)
	{

	}
	else
	{
		gfn_popup_message("modal", "error", "취소할 항목이 없습니다. 항목을 먼저 체크하세요.", "확인");
		bValidate = false;		

	}

	// 정상일경우 처리
	if(bValidate == true)
	{
		// 저장 
		if(gfn_popup_message("modal", "confirm", "선택된 항목을  결재요청 취소 하시겠습니까?", "확인")) 
		{

			// 새로운 DataSet에 담아서 보내야됨
			// 아니면 변경된 모든것들을 보내버리므로 안됨.
			ds_not_lst.ClearData();
			ds_not_lst.CopyF("DS_TIEUPPAY_SIN_LST");

			svcID			= "ACAD02T01.deleteTieupPayLst";
			url   			= "service::common/select_mi.do";
			inDatasets  	= "ac.ACAffairDAO.deleteTieupPayLst=ds_not_lst:A";
			outDatasets 	= "";
			argument    	= " _sqlName=ac.ACAffairDAO.deleteTieupPayLst";

			callbackFunc 	= "gfn_callback";	


			gfn_showProgressWindow(true);
			gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	 
		}

	}

	DS_TIEUPPAY_SIN_LST.UnFilter();
	DS_TIEUPPAY_SIN_LST.FireEvent = true;	
}


