//* parameter    : 
//* return value : ¾øÀ½
//* ºñ°í         :
//*-----------------------------------------------------------------------------------------------
function btn_add_OnClick(obj)
{
	var vRow = DS_PAY_LST.AddRow();

	// ±âÃÊ°ª
	DS_PAY_LST.SetColumn(vRow,"FLAG","I");	// Ãß°¡ FLAG ³Ö±â
	DS_PAY_LST.SetColumn(vRow,"ROW_ID","");	// ROW_ID(Á¶È¸ÈÄ ¼ÂÆÃµÊ)
	DS_PAY_LST.SetColumn(vRow,"OPERATOR", GDS_USER_INFO.GetColumn(0, "USR_ID"));// µî·ÏÀÚ, ¼öÁ¤ÀÚ ³Ö±â

    DS_PAY_LST.SetColumn(vRow,"BRCH_ID", GDS_USER_INFO.GetColumn(0, "BRCH_ID"));// 
	DS_PAY_LST.SetColumn(vRow,"PAY_DT", gfnGetToday(''));// // ÀÔ±ÝÀÏÀÚ ¿À´Ã 



}


//*------------------------------------------------------------------------------------------------
//* ÇÔ¼ö¼³¸í     : ÀÔ±Ý»èÁ¦
//* ÇÔ¼öÀ¯Çü     : 
//* parameter    : 
//* return value : ¾øÀ½
//* ºñ°í         :
//*-----------------------------------------------------------------------------------------------
function btn_del_OnClick(obj)
{

	// µî·ÏÀÌ ¾Æ´Ï¸é »èÁ¦ ºÒ°¡ 
	if(DS_PAY_LST.GetColumn(DS_PAY_LST.currow,"SIN_GBN") == '20' || DS_PAY_LST.GetColumn(DS_PAY_LST.currow,"SIN_GBN") == '30')
	{
		gfn_popup_message("modal", "error", "°áÀç ÁøÇà»óÅÂ°¡ °áÀçÁß, °áÀç¿Ï·á ÀÏ¶§´Â »èÁ¦°¡ ºÒ°¡´É ÇÕ´Ï´Ù.", "È®ÀÎ");
		return;
	}


	//  È®ÀÎ
	if(gfn_popup_message("modal", "confirm", "¼±ÅÃµÈ ÇàÀ» »èÁ¦ ÇÏ½Ã°Ú½À´Ï±î?", "È®ÀÎ") == false) 
	{
		return;
	}

	// Ãß°¡µÈ°ÍÀº DBÀúÀåÀüÀÌ¹Ç·Î ±×³É DataSet¿¡¼­ »èÁ¦
	if(DS_PAY_LST.GetColumn(DS_PAY_LST.curRow,"FLAG") == "I")
	{
		DS_PAY_LST.DeleteRow(DS_PAY_LST.curRow);
	}
	// ¾Æ´Ñ°ÍÀº ½ÇÁ¦·Î DB¿¡ »èÁ¦ÇØ¾ßµÇ¹Ç·Î flag¸¦ D·Î µÎ°í ¸¶Áö¸·¿¡ filterÇÑ´Ù.
	else
	{
		DS_PAY_LST.SetColumn(DS_PAY_LST.curRow,"FLAG", "D");
	}	

	DS_PAY_LST.Filter("flag != 'D'");	// »èÁ¦µÈ°Å´Â ÇÊÅÍ¸µ

	fn_save();
}

