	// 주민번호 공개
	gfn_comCodeCombo(COB_JMN_OPN_YN, "AD19", "","");


	gfn_comCodeCombo(COB_FORM_GBN, "AC10", "","");



	// 도장반출구분
	gfn_comCodeCombo(COB_STP_OUT_GBN, "AD11", "","");



	// 등록구분
	gfn_comCodeCombo(S_REG_GBN, "AC11", "","전체");
	gfn_comCodeCombo(COB_REG_GBN, "AC11", "","");

	// 간편출력
	gfn_comCodeCombo(COB_PRT_GBN, "CC09", "","");



	// 서류접수방법
	gfn_makeCodeDataSet("AC09", "", "DS_ACP_MTD");   // 



	// Grid의 컬럼에 매핑 해보자 .
	gfn_setGridCombo(GRD_BONDSPOT_LST, "DS_ACP_MTD", 9);


