	S_BND_NM.SetFocus();


	// 팝업창에서 오면 바로 조회
	if(P_BRCH_ID != "" && P_BND_SEQ != "")
	{
		fn_search_one(P_BRCH_ID, P_BND_SEQ);
	}
	else
	{
		fn_search();
	}

	fn_bond_edit(false);

}


//*------------------------------------------------------------------------------------------------
//* 함수설명     : 1.2.0 조회 함수 (전체)
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         : 데이터셋 clear 후 조회 (만약 안하면 기존 데이터 남아서 조회값이 없어도 있는것 처럼 보임)
//*-----------------------------------------------------------------------------------------------
function fn_search()
{

	DS_BOND_TREE.ClearData();

	svcID			= "CCAA02T01.selectBondTree";
	url   			= "service::common/select_mi.do";
	inDatasets  	= "";
	outDatasets 	= "DS_BOND_TREE=cc.CCBondDAO.selectBondTree"
							;	
	;
	argument    	= " _sqlName=cc.CCBondDAO.selectBondTree"
							+ " S_BRCH_ID=" + quote(GDS_USER_INFO.BRCH_ID)
							+ " S_BND_NM=" + quote(S_BND_NM.Text)
							+ " S_BND_TEL=" + quote(S_BND_TEL.Text)
							+ " S_REG_GBN=" + quote(S_REG_GBN.Value)
							+ " S_FORM_GBN=" + quote(S_FORM_GBN.Value)
							+ " OPERATOR=" + quote(GDS_USER_INFO.USR_ID);							



	callbackFunc 	= "gfn_callback";

	gfn_showProgressWindow(true);
	gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);;		
}


//*------------------------------------------------------------------------------------------------
//* 함수설명     : 1.2.1 조회 함수 (트랜젝션 일어난후 조회)
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         : 트랜젝션이 일어난 후 조회는 데이터셋을 clear 안하고 조회만 한다.
//*-----------------------------------------------------------------------------------------------
function fn_search_one(brch_id, bnd_seq)
{

	DS_BOND_LST.ClearData();
	DS_BONDSPOT_LST.ClearData();

	curSpotRow = DS_BONDSPOT_LST.curRow;

	svcID			= "CCAA02T01.selectBondOne";
	url   			= "service::common/select_mi.do";
	inDatasets  	= "";
	outDatasets 	= "DS_BOND_LST=cc.CCBondDAO.selectBondOne"
							+ " DS_BONDSPOT_LST=cc.CCBondDAO.selectBondSpotOne"
							;	
	;
	argument    	= " _sqlName=cc.CCBondDAO.selectBondOne"
							+ ",cc.CCBondDAO.selectBondSpotOne"
							+ " S_BRCH_ID=" + quote(brch_id)
							+ " S_BND_SEQ=" + quote(bnd_seq)
							+ " OPERATOR=" + quote(GDS_USER_INFO.USR_ID);							



	callbackFunc 	= "gfn_callback";

	gfn_showProgressWindow(true);
	gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	 	

}




//*------------------------------------------------------------------------------------------------
//* 함수설명     : 채권사 정보 저장 함수 
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function fn_save()
{
	curSpotRow = DS_BONDSPOT_LST.curRow;

	var bValidate = true;
	var pMessage = "";
	var cMessage = "";
	var isSave = false;

	// 필터 
	DS_BOND_LST.FireEvent = false;		
	DS_BOND_LST.UnFilter();
	DS_BOND_LST.Filter("FLAG !='N'");

	DS_BONDSPOT_LST.FireEvent = false;		
	DS_BONDSPOT_LST.UnFilter();
	DS_BONDSPOT_LST.Filter("FLAG !='N'");

	if(DS_BOND_LST.rowcount > 0)
	{
		// 필수체크
		for(var i=0;i<DS_BOND_LST.rowcount;i++)
		{

			cMessage = ""; 


			if(length(DS_BOND_LST.GetColumn(i, "BND_NM")) == 0)
			{
				bValidate = false;
				cMessage += "채권사명 누락 ";
			}

			if(length(DS_BOND_LST.GetColumn(i, "BND_SEQ")) == 0)
			{
				bValidate = false;
				cMessage += "채권사코드 누락 ";
			}			

			if(length(DS_BOND_LST.GetColumn(i, "ZONE_GBN")) == 0)
			{
				bValidate = false;
				cMessage += "구역 누락 ";
			}


			if(length(DS_BOND_LST.GetColumn(i, "CHRG_AMT")) == 0)
			{
				bValidate = false;
				cMessage += "수수료 누락 ";
			}			




			if(length(DS_BOND_LST.GetColumn(i, "FORM_GBN")) == 0)
			{
				bValidate = false;
				cMessage += "양식파일구분 누락 ";
			}		


			/*
			if(length(DS_BOND_LST.GetColumn(i, "ACP_MTD")) == 0)
			{
				bValidate = false;
				cMessage += "접수방법 누락 ";
			}
			*/		

			if(bValidate == false)
			{
				pMessage += "\n ["+i+"] " + cMessage;
			}
		}
	}

	if(bValidate == false)
	{
		gfn_popup_message("modal", "error", cMessage, "확인");
	}
	else
	{
		isSave = true;
	}	

	// 지점정보체크 
	if(DS_BONDSPOT_LST.rowcount > 0)
	{

		// 필수체크
		for(var i=0;i<DS_BONDSPOT_LST.rowcount;i++)
		{

			cMessage = "";

			if(length(DS_BONDSPOT_LST.GetColumn(i, "SPT_NM")) == 0)
			{
				bValidate = false;
				cMessage += "지점명 누락 ";
			}

			/*
			if(length(DS_BONDSPOT_LST.GetColumn(i, "ACP_MTD")) == 0)
			{
				bValidate = false;
				cMessage += "접수방법 누락 ";
			}
			*/

			if(bValidate == false)
			{
				pMessage += "\n ["+i+"] " + cMessage;
			}
		}

		// 필수항목 누락 
		if(bValidate == false)
		{
			gfn_popup_message("modal", "error", pMessage, "확인");
		}
		else
		{
			isSave = true;
		}	
	}


	// 정상일경우 처리
	if(bValidate == true)
	{
		// 저장 
		//if(gfn_popup_message("modal", "confirm", "저장하시겠습니까?", "확인")) 
		//{


			// 저장할 항목이 있을때만 submit 한다.
			// 아니면 패스~
			if(DS_BOND_LST.rowcount > 0 || DS_BONDSPOT_LST.rowcount > 0)
			{			

				// 새로운 DataSet에 담아서 보내야됨
				// 아니면 변경된 모든것들을 보내버리므로 안됨.
				DS_BND_TEMP.ClearData();
				DS_BND_TEMP.CopyF("DS_BOND_LST");

				DS_BNDSPOT_TEMP.ClearData();
				DS_BNDSPOT_TEMP.CopyF("DS_BONDSPOT_LST");


				svcID			= "CCAA02T01.saveBondLst";
				url   			= "service::common/select_mi.do";
				inDatasets  	= "cc.CCBondDAO.saveBondLst=DS_BND_TEMP:A"
								+ " cc.CCBondDAO.saveBondSpotLst=DS_BNDSPOT_TEMP:A";

				outDatasets 	= "";
				argument    	= " _sqlName=cc.CCBondDAO.saveBondLst"
								+ ",cc.CCBondDAO.saveBondSpotLst"			
								;
				callbackFunc 	= "gfn_callback";	


				gfn_showProgressWindow(true);
				gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	

			}
		//}

	}

	DS_BOND_LST.UnFilter();
	DS_BOND_LST.Filter("flag != 'D'");
	DS_BOND_LST.FireEvent = true;

	DS_BONDSPOT_LST.UnFilter();
	DS_BONDSPOT_LST.Filter("flag != 'D'");
	DS_BONDSPOT_LST.FireEvent = true;	


	DS_BONDSPOT_LST.row = curSpotRow;	

	fn_bondspot_lst();

}




//*------------------------------------------------------------------------------------------------
//* 함수설명     : 1.4 삭제 함수 
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function fn_delete()
{
	var bValidate = true;
	var pMessage = "";
	var cMessage = "";

	// 선택된 채권사 삭제 
	if(length(DS_BOND_LST.GetColumn(DS_BOND_LST.currow,"ROW_ID")) == 0)
	{
		gfn_popup_message("modal", "error", "삭제할 항목이 없습니다.", "확인");
		bValidate = false;	
		return;
	}

	// 정상일경우 처리
	if(bValidate == true)
	{
		//삭제 
		//if(gfn_popup_message("modal", "confirm", "삭제하시겠습니까? 삭제 후 복원이 불가능 합니다.", "확인")) 
		//{

			svcID			= "CCAA02T01.deleteBondLst";
			url   			= "service::common/select_mi.do";
			inDatasets  	= "";
			outDatasets 	= "";
			argument    	= " _sqlName=cc.CCBondDAO.deleteBondLst"
							+ " ROW_ID=" + quote(DS_BOND_LST.GetColumn(DS_BOND_LST.currow,"ROW_ID"))
							;

			callbackFunc 	= "gfn_callback";	


			gfn_showProgressWindow(true);
			gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	 
		//}

	}
}



//*------------------------------------------------------------------------------------------------
//* 함수설명     : 1.5 Transaction 호출후 결과값 확인 (gfn_callback에서 fn_callback을 호출한다.)
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function fn_callback(svcID, errorCode, errorMsg)
{

	switch(svcID) 
	{		
		// TREE
		case "CCAA02T01.selectBondTree" :



		break;		

		// 조회
		case "CCAA02T01.selectBondOne" :

			// 조회 결과 없으면 메시지를 띄운다.
			//if(DS_BOND_LST.rowcount < 1) 
			//{ 
			//	gfn_popup_message("modal", "ico_info", "조회된 내용이 없습니다.", "확인");	
			//}

			fn_bond_edit(false);

			fn_bondspot_lst();

		break;	


		// 수정
		case "CCAA02T01.saveBondLst" :
			gfn_setMsg("채권사 추가/수정 완료: " + DS_BOND_LST.GetColumn(0,"BND_NM"));
			fn_search_one(DS_BOND_TREE.GetColumn(DS_BOND_TREE.currow,"BRCH_ID"), DS_BOND_TREE.GetColumn(DS_BOND_TREE.currow,"BND_SEQ"));		
		break;	


		// 삭제
		case "CCAA02T01.deleteBondLst" :

			gfn_setMsg("채권사 삭제 완료.");
			fn_search_one(DS_BOND_TREE.GetColumn(DS_BOND_TREE.currow,"BRCH_ID"), DS_BOND_TREE.GetColumn(DS_BOND_TREE.currow,"BND_SEQ"));		
		break;	



		default:
	}



	if(curSpotRow != "") 
	{
		DS_BONDSPOT_LST.row = curSpotRow;
	}




}



