function fn_bondspot_lst()
{



	// 해당 채권사번호 필터 
	DS_BONDSPOT_LST.FireEvent = false;		
	DS_BONDSPOT_LST.UnFilter();
	DS_BONDSPOT_LST.Filter("flag != 'D'");	

	// 삭제된거는 필터링
	//DS_BOND_LST.Filter("flag != 'D'");

	DS_BONDSPOT_LST.FireEvent = true;

	DS_BONDSPOT_LST.row = curSpotRow;	


	// 양식에 따른 체크
	fn_btn_control();

}



//*------------------------------------------------------------------------------------------------
//* 함수설명     : 채권사 코드 채번
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function fn_get_bnd_seq()
{

	svcID			= "CCAA02T01.selectBndSeq";
	url   			= "service::common/select_mi.do";
	inDatasets  	= "";
	outDatasets 	= "DS_BND_SEQ=cc.CCBondDAO.selectBndSeq";	

	argument    	= " _sqlName=cc.CCBondDAO.selectBndSeq";	

	callbackFunc 	= "gfn_callback";

	gfn_SyncTransaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	

	return DS_BND_SEQ.GetColumn(0, "BND_SEQ");    


}



//*------------------------------------------------------------------------------------------------
//* 함수설명     : 파일업로드 
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function fn_file_upload()
{
	// 사진추가된 경우
	if(length(File1.FileName) != 0)
	{
		var tmpFileName =  FileDialog1.FileName;			// 파일명
		var chkFile = "";									// 임시 파일명
		var fileExt = "";										// 확장자 

		fileExt = gfn_getExt(tmpFileName);

		// MRD 파일만 저장되게
		if(ToUpper(fileExt) != 'MRD')
		{
			gfn_popup_message("modal", "ico_alert", "양식 파일은 MRD(Report Designer) 파일 형식이어야 합니다.", "확인");
			return;
		}		

		// 1. 사진 저장
		var filefullname = File1.FileName;
		var savefilename = "FORM_" + DS_BOND_LST.GetColumn(DS_BOND_LST.curRow, "BND_SEQ") +"."+fileExt; 
		var savepath  = bondPath + savefilename;

		var ret = gfn_FileWrite(multipart_http0, filefullname, savefilename, savepath);

		// 업로드 실패
		if(ret[0] == "FAIL")
		{
			gfn_popup_message("modal", "ico_alert", "양식파일 저장 실패하였습니다.", "확인");
		}
		// 업로드 성공
		else
		{
			DS_BOND_LST.SetColumn(DS_BOND_LST.curRow, "FORM_FILE", FileDialog1.FileName);		// 원본파일명
			DS_BOND_LST.SetColumn(DS_BOND_LST.curRow, "FORM_SAVE_FILE", savefilename);			// 저장파일명
		}
	}


//	File1.Delete();
	File1.Close();	

}


