function GRD_BONDSPOT_LST_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	curSpotRow = nRow;

	// 열려진 창이 있으면 해당창에 포커스 
	var oForm = global.AllWindows["CPAD01P01"];
	if(oForm != null)
	{
		oForm[0].setFocus();
	}
	else
	{
		// 에디터 팝업 띄움
		gfn_openPopup(this, "CP::CPAD01P01.xml", "" , false);
	}	

}

