//*------------------------------------------------------------------------------------------------
//* 함수설명     : 파일첨부  실행 스크립트
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function BTN_FILE_SAVE_OnClick(obj)
{

	// 기본정보가 선택되지 않으면 경고 
	if(length(DS_BOND_LST.GetColumn(DS_BOND_LST.curRow,"BND_SEQ")) == 0)
	{
		gfn_popup_message("modal", "error", "먼저 채권사 기본정보를 선택 해야 합니다. ", "확인");
		return;
	}	

	FileDialog1.Type = "OPEN";

	if(FileDialog1.Open()){

		var fileExt = gfn_getExt(FileDialog1.FileName);

		// MRD 파일만 저장되게
		if(ToUpper(fileExt) != 'MRD')
		{
			gfn_popup_message("modal", "ico_alert", "양식 파일은 MRD(Report Designer) 파일 형식이어야 합니다.", "확인");
			return;
		}



		File1.FileName = FileDialog1.FilePath + "\\" + FileDialog1.FileName;

		DS_BOND_LST.SetColumn(DS_BOND_LST.curRow, "FORM_FILE", FileDialog1.FileName);


		// 바로 저장 
		fn_file_upload();

	}	
}

//*------------------------------------------------------------------------------------------------
//* 함수설명     : 파일다운  실행 스크립트
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function BTN_FILE_DOWN_OnClick(obj)
{

	// 기본정보가 선택되지 않으면 경고 
	if(length(DS_BOND_LST.GetColumn(DS_BOND_LST.curRow,"BND_SEQ")) == 0)
	{
		gfn_popup_message("modal", "error", "먼저 채권사 기본정보를 선택 해야 합니다. ", "확인");
		return;
	}	

	var str_param = DS_BOND_LST.GetColumn(DS_BOND_LST.curRow, "FORM_SAVE_FILE");

	FileDialog1.Type = "save";
	//FileDialog1.FileName = str_param;	
	FileDialog1.Open();	
	//trace(" >>> " + FileDialog1.FilePath);

	var savefilename = str_param;
	var savepath  = bondPath + savefilename;

	var ret = gfn_ReadFile(multipart_http0, FileDialog1.FilePath +"\\"+ FileDialog1.FileName, savepath, 0);

	if ( ret[0] == "FAIL" )
	{
		alert('오류:'+ret[1]);
	} 	
}

