//*------------------------------------------------------------------------------------------------
//* 함수설명     : 추가 클릭시  실행 스크립트
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function btn_add_OnClick(obj)
{

	var vRow = DS_BOND_LST.AddRow();
    var BND_SEQ = fn_get_bnd_seq();

	// 기초값
	DS_BOND_LST.SetColumn(vRow,"FLAG","I");	// 추가 FLAG 넣기
	DS_BOND_LST.SetColumn(vRow,"ROW_ID","");	// ROW_ID(조회후 셋팅됨)
	DS_BOND_LST.SetColumn(vRow,"OPERATOR", GDS_USER_INFO.GetColumn(0, "USR_ID"));// 등록자, 수정자 넣기

    DS_BOND_LST.SetColumn(vRow,"BRCH_ID", GDS_USER_INFO.GetColumn(0, "BRCH_ID"));// 
    DS_BOND_LST.SetColumn(vRow,"BND_SEQ", BND_SEQ);// 


	fn_bondspot_lst();



}






//*------------------------------------------------------------------------------------------------
//* 함수설명     : 행추가 클릭시  실행 스크립트
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function btn_add_dtl_OnClick(obj)
{

	// 기본정보가 선택되지 않으면 경고 
	if(length(DS_BOND_LST.GetColumn(DS_BOND_LST.curRow,"BND_SEQ")) == 0)
	{
		gfn_popup_message("modal", "error", "먼저 채권사 기본정보를 선택 해야 합니다. ", "확인");
		return;
	}

	var vRow = DS_BONDSPOT_LST.AddRow();

	// 기초값
	DS_BONDSPOT_LST.SetColumn(vRow,"FLAG","I");	// 추가 FLAG 넣기
	DS_BONDSPOT_LST.SetColumn(vRow,"ROW_ID","");	// ROW_ID(조회후 셋팅됨)
	DS_BONDSPOT_LST.SetColumn(vRow,"OPERATOR", GDS_USER_INFO.GetColumn(0, "USR_ID"));// 등록자, 수정자 넣기

    DS_BONDSPOT_LST.SetColumn(vRow,"BRCH_ID", DS_BOND_LST.GetColumn(DS_BOND_LST.curRow, "BRCH_ID"));// 
    DS_BONDSPOT_LST.SetColumn(vRow,"BND_SEQ", DS_BOND_LST.GetColumn(DS_BOND_LST.curRow, "BND_SEQ"));// 

    // 채권사의 값도 수정처리	
	DS_BOND_LST.SetColumn(0,"FLAG", "U");


}


//*------------------------------------------------------------------------------------------------
//* 함수설명     : 행삭제 클릭시  실행 스크립트
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function btn_del_dtl_OnClick(obj)
{
	// 추가된것은 DB저장전이므로 그냥 DataSet에서 삭제
	if(DS_BONDSPOT_LST.GetColumn(curSpotRow,"FLAG") == "I")
	{
		DS_BONDSPOT_LST.DeleteRow(curSpotRow);
	}
	// 아닌것은 실제로 DB에 삭제해야되므로 flag를 D로 두고 마지막에 filter한다.
	else
	{
		DS_BONDSPOT_LST.SetColumn(curSpotRow,"FLAG", "D");
	}	

	// 채권사의 값도 수정처리	
	DS_BOND_LST.SetColumn(0,"FLAG", "U");


	fn_bondspot_lst();

}


//*------------------------------------------------------------------------------------------------
//* 함수설명     : DataSet 변경시 자동저장
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function DS_BOND_LST_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	// flag가 'N' 이면 'U' 로 바꾼다.
	if(obj.GetColumn(nRow, "FLAG") == "N" )
	{
		obj.SetColumn(nRow, "FLAG", "U");	// 수정했다는 표시 
	}




}



function DS_BONDSPOT_LST_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	// flag가 'N' 이면 'U' 로 바꾼다.
	if(obj.GetColumn(nRow, "FLAG") == "N" )
	{
		obj.SetColumn(nRow, "FLAG", "U");	// 수정했다는 표시 

		// 채권사의 값도 수정처리	
		DS_BOND_LST.SetColumn(0,"FLAG", "U");

	}


}


//*------------------------------------------------------------------------------------------------
//* 함수설명     : MRD 테스트
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function btn_mrd_test_OnClick(obj)
{
	// 미저장되면 경고
	if(fn_check_unsave() == true)
	{
		gfn_popup_message("modal", "error", "먼저 정보를 저장 하고 테스트 해야 합니다. ", "확인");
		return;
	}

	// 첨부파일이 등록안되면 경고
	if(length(DS_BOND_LST.GetColumn(DS_BOND_LST.currow, "FORM_SAVE_FILE")) == 0)
	{
		gfn_popup_message("modal", "error", "첨부파일이 없습니다. ", "확인");
		return;
	}	


	var params = "P_FORM_SAVE_FILE="+quote(DS_BOND_LST.GetColumn(DS_BOND_LST.currow, "FORM_SAVE_FILE")) ;

	var retArr = gfn_openPopup(this, "CP::CPAB02P01.xml", params, false);


}



// 양식구분 변경시
function COB_FORM_GBN_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fn_btn_control();
}


// 버튼 컨트롤
function fn_btn_control()
{
	if(DS_BOND_LST.GetColumn(0,"FORM_GBN") == "C" || DS_BOND_LST.GetColumn(0,"FORM_GBN") == "N")
	{
		BTN_FILE_SAVE.Enable = false;
		BTN_FILE_DOWN.Enable = false;
		BTN_FILE_DEL.Enable = false;
		btn_mrd_test.Enable = false;
	}
	else
	{
		BTN_FILE_SAVE.Enable = true;
		BTN_FILE_DOWN.Enable = true;
		BTN_FILE_DEL.Enable = true;
		btn_mrd_test.Enable = true;
	}
}





