function fn_acp_chg_popup()
{

	// 기본정보가 선택되지 않으면 경고 
	if(length(DS_BOND_LST.GetColumn(DS_BOND_LST.curRow,"BND_SEQ")) == 0)
	{
		gfn_popup_message("modal", "error", "신규추가 또는 좌측목록의 채권사를 선택 해야 합니다. ", "확인");
		return;
	}

	var params = "P_BRCH_ID=" + quote(GDS_USER_INFO.BRCH_ID)
			   + " P_USR_NM=" + quote(EDT_ACP_CHG_USR_NM.Text);


	var retArr = gfn_openPopup(this, "CP::CPAB04P01.xml", params, true);

	DS_BOND_LST.SetColumn(DS_BOND_LST.curRow,"ACP_CHG_USR", retArr[0]);
	DS_BOND_LST.SetColumn(DS_BOND_LST.curRow,"ACP_CHG_USR_NM", retArr[1]);

}


