// 수정가능 유무 설정
function fn_bond_edit(isEdit)
{
	//  수정가능 유무
	if(isEdit == false)
	{
		EDT_BND_NM.Readonly = true;
		EDT_BND_FUL_NM.Readonly = true;
		EDT_BND_TEL.Readonly = true;
		EDT_BND_FAX.Readonly = true;
		EDT_BND_ZIP.Readonly = true;
		EDT_BND_ADDR.Readonly = true;
		EDT_ACC_NO.Readonly = true;
		MAE_CHRG_AMT.Readonly = true;
		EDT_ISU_EXP_DD.Readonly = true;
		EDT_ACP_CHG_USR_NM.Readonly = true;
		TXT_RLT_NM.Readonly = true;
		TXT_BUY_BND.Readonly = true;
		TXT_SEL_ROUT.Readonly = true;
		TXT_ISU_TIP.Readonly = true;
		TXT_ISU_NOTE.Readonly = true;
		TXT_FORM_NOTE.Readonly = true;

		COB_ZONE_GBN.Enable = false;
		COB_DT_GBN.Enable = false;
		COB_STP_OUT_GBN.Enable = false;
		COB_BIZ_PRT_YN.Enable = false;
		COB_PRT_GBN.Enable = false;
		COB_FORM_GBN.Enable = false;
		COB_ACP_MTD.Enable = false;
		COB_REG_GBN.Enable = false;
		COB_CLT_OPN_YN.Enable = false;
		COB_JMN_OPN_YN.Enable = false;

		BTN_FILE_SAVE.Enable = false;
		BTN_FILE_DEL.Enable = false;
		POP_ACP_CHG_USR.Visible = false;
	}
	else
	{
		EDT_BND_NM.Readonly = false;
		EDT_BND_FUL_NM.Readonly = false;
		EDT_BND_TEL.Readonly = false;
		EDT_BND_FAX.Readonly = false;
		EDT_BND_ZIP.Readonly = false;
		EDT_BND_ADDR.Readonly = false;
		EDT_ACC_NO.Readonly = false;
		MAE_CHRG_AMT.Readonly = false;
		EDT_ISU_EXP_DD.Readonly = false;
		EDT_ACP_CHG_USR_NM.Readonly = false;
		TXT_RLT_NM.Readonly = false;
		TXT_BUY_BND.Readonly = false;
		TXT_SEL_ROUT.Readonly = false;
		TXT_ISU_TIP.Readonly = false;
		TXT_ISU_NOTE.Readonly = false;
		TXT_FORM_NOTE.Readonly = false;

		COB_ZONE_GBN.Enable = true;
		COB_DT_GBN.Enable = true;
		COB_STP_OUT_GBN.Enable = true;
		COB_BIZ_PRT_YN.Enable = true;
		COB_PRT_GBN.Enable = true;
		COB_FORM_GBN.Enable = true;
		COB_ACP_MTD.Enable = true;
		COB_REG_GBN.Enable = true;
		COB_CLT_OPN_YN.Enable = true;
		COB_JMN_OPN_YN.Enable = true;

		BTN_FILE_SAVE.Enable = true;
		BTN_FILE_DEL.Enable = true;
		POP_ACP_CHG_USR.Visible = true;


	}

}
