function grd_ntboard_mst_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var flag = "read";

	//alert(GDS_USER_INFO.eno+" / "+ds_rtmboard_mst.GetColumn(nRow,"eno"));

	if(GDS_USER_INFO.eno == ds_ntboard_mst.GetColumn(nRow,"eno")){
		flag = "modify";
	}

	var params = " p_flag="+quote(flag);
	var flags = gfn_openPopup(this, "COMM::BOAD01P01.xml", params, true);
}

//*------------------------------------------------------------------------------------------------
//* 함수설명     : 엔터키 다운 이벤트
