//* return value : 없음
//*-----------------------------------------------------------------------------------------------
function edt_search_condition_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar==13){
		btn_search_OnClick();
	}
}
