function fn_initForm()
{


	fn_search();

}


//*------------------------------------------------------------------------------------------------
//* 함수설명     : 1.2.0 조회 함수 (전체)
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         : 데이터셋 clear 후 조회 (만약 안하면 기존 데이터 남아서 조회값이 없어도 있는것 처럼 보임)
//*-----------------------------------------------------------------------------------------------
function fn_search()
{
	DS_EVP_LST.ClearData();

	curEvpRow = DS_EVP_LST.curRow;

	svcID			= "CPAB06P03.selectEvpLst";
	url   			= "service::common/select_mi.do";
	inDatasets  	= "";
	outDatasets 	= "DS_EVP_LST=ac.ACEvpDAO.selectEvpLst"
							;	

	argument    	= " _sqlName=ac.ACEvpDAO.selectEvpLst"
							+ " S_BRCH_ID=" + quote(P_BRCH_ID)
							+ " S_BND_NM=" + quote(S_BND_NM.Text)
							+ " OPERATOR=" + quote(GDS_USER_INFO.USR_ID);							

	callbackFunc 	= "gfn_callback";

	gfn_showProgressWindow(true);
	gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	 
}




//*------------------------------------------------------------------------------------------------
//* 함수설명     : 채권사 정보 저장 함수 
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function fn_save()
{

	var bValidate = true;
	var pMessage = "";
	var cMessage = "";
	var isSave = false;

	curEvpRow = DS_EVP_LST.curRow;		

	DS_EVP_LST.FireEvent = false;		
	DS_EVP_LST.UnFilter();
	DS_EVP_LST.Filter("FLAG !='N'");

	// 채권사 
	if(DS_EVP_LST.rowcount > 0)
	{

		// 필수체크
		for(var i=0;i<DS_EVP_LST.rowcount;i++)
		{

			cMessage = "";

			if(length(DS_EVP_LST.GetColumn(i, "BND_NM")) == 0)
			{
				bValidate = false;
				cMessage += "채권사명 누락 ";
			}

			if(bValidate == false)
			{
				pMessage += "\n ["+i+"] " + cMessage;
			}
		}

		// 필수항목 누락 
		if(bValidate == false)
		{
			gfn_popup_message("modal", "error", pMessage, "확인");
		}
		else
		{
			isSave = true;
		}	
	}


	// 정상일경우 처리
	if(bValidate == true)
	{
			// 새로운 DataSet에 담아서 보내야됨
			// 아니면 변경된 모든것들을 보내버리므로 안됨.
			DS_TEMP.ClearData();
			DS_TEMP.CopyF("DS_EVP_LST");


			svcID			= "CPAB06P03.saveEvpLst";
			url   			= "service::common/select_mi.do";
			inDatasets  	= "ac.ACEvpDAO.saveEvpLst=DS_TEMP:A"
							;

			outDatasets 	= "";
			argument    	= " _sqlName=ac.ACEvpDAO.saveEvpLst"
							;
			callbackFunc 	= "gfn_callback";	


			gfn_showProgressWindow(true);
			gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	 

	}

	DS_EVP_LST.UnFilter();
	DS_EVP_LST.Filter("flag != 'D'");
	DS_EVP_LST.FireEvent = true;	

	DS_EVP_LST.row = curEvpRow;


}



//*------------------------------------------------------------------------------------------------
//* 함수설명     : 1.5 Transaction 호출후 결과값 확인 (gfn_callback에서 fn_callback을 호출한다.)
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function fn_callback(svcID, errorCode, errorMsg)
{

	switch(svcID) 
	{		

		//  봉투출력 조회 
		case "CPAB06P03.selectEvpLst" :

		break;	




		//  봉투출력 저장
		case "CPAB06P03.saveEvpLst" :
			gfn_setMsg("봉투출력 채권사 추가/수정/삭제 완료.");
			fn_search();		
		break;	



		default:
	}


	if(curEvpRow != null) 
	{
		DS_EVP_LST.row = curEvpRow;
	}

}



