//*------------------------------------------------------------------------------------------------
//* 함수설명     : 2.3출력
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function btn_print_OnClick(obj)
{
	var mrd = "";
	var param = "";

	// 선택된 행의 ROWID가 없으면 즉 저장 안된 상태면 경고창
	if(length(DS_EVP_LST.GetColumn(DS_EVP_LST.curRow,"ROW_ID")) == 0)
	{
		gfn_popup_message("modal", "error", "먼저 선택된 채권사를 저장 후 출력 하세요. ", "확인");
		return;
	}

	mrd = "PG_AC_EVP_LST.mrd";


	param = "/rp ["
			+DS_EVP_LST.GetColumn(DS_EVP_LST.curRow, "BND_NM")+"] ["        // 채권사명
			+DS_EVP_LST.GetColumn(DS_EVP_LST.curRow, "BND_ADDR")+"]"  // 주소
			+DS_EVP_LST.GetColumn(DS_EVP_LST.curRow, "BND_ZIP")+"]"  // 우편번호
			;

	trace('rp param='+param);		


	gfn_viewReport(Report0, mrd, param, true);	

}





