//*------------------------------------------------------------------------------------------------
//* 함수설명     : 행추가 클릭시  실행 스크립트
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function btn_add_dtl_OnClick(obj)
{

	// 기본정보가 선택되지 않으면 경고 
	if(length(P_BRCH_ID) == 0)
	{
		gfn_popup_message("modal", "error", "회사정보 누락 관리자에게 문의하세요.. ", "확인");
		return;
	}

	var vRow = DS_EVP_LST.AddRow();

	// 기초값
	DS_EVP_LST.SetColumn(vRow,"FLAG","I");	// 추가 FLAG 넣기
	DS_EVP_LST.SetColumn(vRow,"ROW_ID","");	// ROW_ID(조회후 셋팅됨)
	DS_EVP_LST.SetColumn(vRow,"OPERATOR", GDS_USER_INFO.GetColumn(0, "USR_ID"));// 등록자, 수정자 넣기

    DS_EVP_LST.SetColumn(vRow,"BRCH_ID", P_BRCH_ID);// 

}


//*------------------------------------------------------------------------------------------------
//* 함수설명     : 행삭제 클릭시  실행 스크립트
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function btn_del_dtl_OnClick(obj)
{
	// 추가된것은 DB저장전이므로 그냥 DataSet에서 삭제
	if(DS_EVP_LST.GetColumn(row,"FLAG") == "I")
	{
		DS_EVP_LST.DeleteRow(DS_EVP_LST.currow);
	}
	// 아닌것은 실제로 DB에 삭제해야되므로 flag를 D로 두고 마지막에 filter한다.
	else
	{
		DS_EVP_LST.SetColumn(DS_EVP_LST.currow,"FLAG", "D");
	}	

	DS_EVP_LST.Filter("flag != 'D'");	// 삭제된거는 필터링

}






//*------------------------------------------------------------------------------------------------
//* 함수설명     : DataSet 변경시 자동저장
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//* 비고         :
//*-----------------------------------------------------------------------------------------------
function DS_EVP_LST_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	// flag가 'N' 이면 'U' 로 바꾼다.
	if(obj.GetColumn(nRow, "FLAG") == "N" )
	{
		obj.SetColumn(nRow, "FLAG", "U");	// 수정했다는 표시 
	}
}





function btn_close_OnClick(obj)
{
