Window.Script:
var fv_ArgValue;
var fv_preHY;
/*----------------------------------------------------------------------*/
//  Calendar Load
/*----------------------------------------------------------------------*/
function form_OnLoadCompleted(obj)
{
	fv_ArgValue = gfv_CalendarValue;

	if(fv_ArgValue.length() == 0) {
		fv_ArgValue = mid(GetDate(),0,6);
	}

	var strYY = mid(fv_ArgValue,0,4);
	var strMM = toInteger(mid(fv_ArgValue,4,2));
	var strHH = "";
	if(strMM > 6)	strHH = "2";
	else	strHH = "1";

	fn_SetCalendar(strYY,strHH);
}

/*----------------------------------------------------------------------*/
//  Calendar Load
/*----------------------------------------------------------------------*/
function fn_SetCalendar(strYY,strHH)
{
	var obj;
	var strId;
	// 색 초기화
	if(trim(fv_preHY).length() > 0) {
		obj = object("H" + toNumber(fv_preHY));
		strId = "h" + fv_preHY;
		obj.ImageID = strId;
	}

	if(length(trim(strHH)) == 0) return;

	// 색 선택
	st_year.Text = strYY;
	obj = object("H" + toNumber(strHH));
	strId = "h" + strHH;
	obj.ImageID = strId + "_R";
	fv_preHY = toNumber(strHH);
}

/*----------------------------------------------------------------------*/
//  Month Click
/*----------------------------------------------------------------------*/
function QuatClick(obj)
{
	var strDate = st_year.Text + lpad(replace(obj.id,"H",""),"0",2);

	ClosePopup(strDate);
}

/*----------------------------------------------------------------------*/
//  Pre Year
/*----------------------------------------------------------------------*/
function img_nextYear_OnClick(obj,nX,nY)
{
	var strYY = st_year.Text;
	st_year.Text = toString(toNumber(strYY) + 1);
	strYY = st_year.Text;

	fn_SetCalendar(strYY,"");		
}

/*----------------------------------------------------------------------*/
//  Next Year
/*----------------------------------------------------------------------*/
function im_preYear_OnClick(obj,nX,nY)
{
	var strYY = st_year.Text;
	st_year.Text = toString(toNumber(strYY) - 1);
	strYY = st_year.Text;
	fn_SetCalendar(strYY,"");	
}


/*----------------------------------------------------------------------*/
//  Close
/*----------------------------------------------------------------------*/
function img_close_OnClick(obj,nX,nY)
{
	ClosePopup();
}

/*----------------------------------------------------------------------*/
//  Close
/*----------------------------------------------------------------------*/
function form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam)
{
	if(nChar == 27) {
		ClosePopup();
	}
}
