Window.Script:
var vCompID = "";

function fn_GetColumnSet_NoPivot(objObject,nRow,strColID)
{


	var strEval =  objObject.id+".GetColumnSet("+nRow+", '"+strColID+")";
	//var Ret = CallScript(strEval);
    var Ret = objObject.GetColumnSet(nRow,strColID);
	var preText = ta_MethodCall_Result.text;
	ta_MethodCall_Result.text = ' var Ret = ' + strEval + chr(10);
	ta_MethodCall_Result.text += "Ret = ";
	ta_MethodCall_Result.text += Ret;
	ta_MethodCall_Result.text += chr(10)+"=================================" + chr(10);			
	for( var i = 0 ; i < Ret.count ; i ++ )
	{
        ta_MethodCall_Result.text += chr(10)+"["+i+"] "+Ret[i].Value;
	}	
	ta_MethodCall_Result.text += chr(10)+"[Count] "+Ret.Count;
	ta_MethodCall_Result.text += chr(10)+"[Sum] "+Ret.Sum;
	ta_MethodCall_Result.text += chr(10)+"[Avg] "+Ret.Avg;
	ta_MethodCall_Result.text += chr(10)+"[Max] "+Ret.Max;
	ta_MethodCall_Result.text += chr(10)+"[Min] "+Ret.Min;

	ta_MethodCall_Result.text += chr(10)+"--------------------------------" + chr(10);			
	ta_MethodCall_Result.text += chr(10)+"=================================" + chr(10);			
	ta_MethodCall_Result.text += preText + chr(10);	    
}

