function fn_SetDataColumn(nRow, vORDID,vORDDate)
{   if(nRow <0)
        nRow = dsOrders.AddRow();
    dsOrders.SetColumn(nRow,'orderid',vORDID);
    dsOrders.SetColumn(nRow,'orderdate',vORDDate);
    dsOrders.SetColumn(nRow,'qty','20');
    dsOrders.SetColumn(nRow,'CATEGORYNAME','가전제품');
    dsOrders.SetColumn(nRow,'DETAILCATEGORYNAME','TV');
    dsOrders.SetColumn(nRow,'MODELNAME','32FD9954');
    dsOrders.SetColumn(nRow,'AGENTNAME','강릉');
    dsOrders.SetColumn(nRow,'BIGAGENTNAME','강원');
    dsOrders.SetColumn(nRow,'UNITCOST','690900');

}

function fn_SetProp_Dataset(objObject, PropID, varNewValue)
{
    trace("fn_SetProp_Dataset ["+objObject+"]["+PropID+"]["+varNewValue+"]");
    switch(PropID)
    {
        case "Argument": objObject.Argument = varNewValue; break;
        case "Auto": objObject.Auto = varNewValue; break;
        case "CodePage": objObject.CodePage = varNewValue; break;
        case "Contents": objObject.Contents = varNewValue; break;
        case "DataObject": objObject.DataObject = varNewValue; break;
        case "DatasetType": objObject.DatasetType = varNewValue; break;
        case "Editable": objObject.Editable = varNewValue; break;
        case "FilterExpr": objObject.FilterExpr = varNewValue; break;
        case "FireEvent": objObject.FireEvent = varNewValue; break;
        case "FireFirstCount": objObject.FireFirstCount = varNewValue; break;
        case "FireNextCount": objObject.FireNextCount = varNewValue; break;
        case "GroupKey": objObject.GroupKey = varNewValue; break;
        case "ID": objObject.ID = varNewValue; break;
        case "NameSpaceInfo": objObject.NameSpaceInfo = varNewValue; break;
        case "PivotKey": objObject.PivotKey = varNewValue; break;
        case "ReverseSubSum": objObject.ReverseSubSum = varNewValue; break;
        case "ServiceDSetID": objObject.ServiceDSetID = varNewValue; break;
        case "ServiceID": objObject.ServiceID = varNewValue; break;
        case "UpdateControl": objObject.UpdateControl = varNewValue; break;
        case "XPath": objObject.XPath = varNewValue; break;
        case "colcount": objObject.colcount = varNewValue; break;
        case "constcount": objObject.constcount = varNewValue; break;
        case "count": objObject.count = varNewValue; break;
        case "currow": objObject.currow = varNewValue; break;
        case "firstrow": objObject.firstrow = varNewValue; break;
        case "lastrow": objObject.lastrow = varNewValue; break;
        case "nextrow": objObject.nextrow = varNewValue; break;
        case "pivotcount": objObject.pivotIndexcount = varNewValue; break;
        case "pivotindex": objObject.pivotindex = varNewValue; break;
        case "prevrow": objObject.prevrow = varNewValue; break;
        case "row": objObject.row = varNewValue; break;
        case "rowcount": objObject.rowcount = varNewValue; break;
        case "rowlevel": objObject.rowlevel = varNewValue; break;
        case "rowpos": objObject.rowpos = varNewValue; break;
        case "rowposition": objObject.rowposition = varNewValue; break;
    }
}


