function gridMethod_OnCellDblClick(obj,nRow,nCell,nX,nY)
{
//   trace("OnCellDblClick : "+nRow+"  "+nCell);
    if((nRow >= 0) &&(nCell < 1))
    {
        var MethodID = dsMethod.GetColumn(nRow,"COMMENT");
        var strEval =  vCompID+"."+MethodID;
        edtScript.Text = strEval;
        edtScript.SetFocus();
    }	
}

