function Tab0_tab2_cboFormat_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
//    trace("Tab0_tab2_cboFormat_OnChanged : "+strCode);
    dsOrderPivot.GroupKey = '';
    dsOrderPivot.PivotKey = '';
	switch(strCode)
	{
        case 'GroupKey1':
        {
            Tab0.tpgPivot.grdPivot.Format = strCode;
            dsOrderPivot.FireEvent = false;
            dsOrderPivot.PivotKey = 'CATEGORYNAME,DETAILCATEGORYNAME,MODELNAME';
            dsOrderPivot.GroupKey = 'orderdate';
            dsOrderPivot.FireEvent = true;
            break;
            //dsOrderPivot.RecalcPivot();
        }
        case 'GroupKey3':
        {
            Tab0.tpgPivot.grdPivot.Format = strCode;
            dsOrderPivot.FireEvent = false;
            dsOrderPivot.PivotKey = 'CATEGORYNAME,DETAILCATEGORYNAME,MODELNAME';
            dsOrderPivot.GroupKey = 'BIGAGENTNAME,AGENTNAME,orderdate';
            dsOrderPivot.FireEvent = true;
            break;
            //dsOrderPivot.RecalcPivot();
        }
        case 'GroupKey4':
        {
            Tab0.tpgPivot.grdPivot.Format = strCode;
            dsOrderPivot.FireEvent = false;
            dsOrderPivot.PivotKey = 'BIGAGENTNAME,AGENTNAME';
            dsOrderPivot.GroupKey = 'CATEGORYNAME,DETAILCATEGORYNAME,MODELNAME,orderdate';
            dsOrderPivot.FireEvent = true;
            break;
            //dsOrderPivot.RecalcPivot();
        }        
	}
}

