//*------------------------------------------------------------------------
//* 함수설명     : 1.1 화면셋팅 함수 
//* 함수유형     :
//* parameter    :
//* return value : 없음
//*------------------------------------------------------------------------
function fn_initForm()
{

	// 콤보박스 
	// 데이터셋이 필요없이 단순하게 콤보만 만드는 함수
	// 해당 함수는 그리드등 다른 오브젝트와 DataSet을 공유하지 않고 일회성 DataSet을 생성한다.
	gfn_comCodeCombo(s_cob_test_code, "P001", "10","전체");

    // 단순 라디오 리스트 
    // 데이터셋이 필요없이 단순하게 라디오리스트만  만드는 함수
	// 해당 함수는 그리드등 다른 오브젝트와 DataSet을 공유하지 않고 일회성 DataSet을 생성한다.
	gfn_comCodeRadio(rad_com_list, "P005","2"); 


	// Dataset을 공유하는 오브젝트 사용법

    // 1. 공통코드용 데이터셋을 생성한다..
    // 생성된 DataSet은 따로 파라미터 지정하지 않아도 사용가능하다.
    gfn_makeCodeDataSet("P002", "", "ds_test_code"); // 공통코드(단순) 용!
    gfn_makeCodeDataSet("P024", "", "ds_code_sel"); // 공통코드(선택) 용!
    gfn_makeCodeDataSet("P008", "", "ds_com_list_2"); // 라디오리스트 


	// 2. 생성된 데이터셋과 콤보 매핑 
	gfn_comCodeDSCombo(cob_test_code, "ds_test_code", "10", "");	// 선택될 값 지정
	gfn_comCodeDSCombo(cob_code_sel, "ds_code_sel", "", "-----");		//  
	gfn_comCodeDSCombo(Tab0.tab2.cob_tap_cod, "ds_code_sel", "", "-----");		//  

    //  3. 생성된 DataSet과 라디오 매핑하기 
    gfn_comCodeDSRadio(rad_com_list_2, "20", "ds_com_list_2");

 	// 4. 일반 데이터셋 생성하기(예: 회사선택)
	// 화면 Loading시 데이터셋은 Sync로 가져온다.
	fn_getTestData();

	gfn_DSCombo(cbo_ds_sel, "ds_test", "COM_ID", "COM_NM",  "", "--회사선택--");

    // DataSet 직접 매핑  
    //gfn_DSRadio(rad_com_list_2, "20", "ds_test", "COM_ID", "COM_NM"); 


	// Grid의 컬럼에 매핑 해보자 .
	gfn_setGridCombo(Tab0.tab2.Grid2, "ds_code_sel", 5);	
	gfn_setGridCombo(Grid1, "ds_test_code", 7);
	gfn_setGridCombo(Grid1, "ds_code_sel", 8);
	gfn_setGridCombo(Grid1, "ds_test", 9, "COM_ID", "COM_NM");

	// 라디오리스트는 Grid의 콤보와 매핑한다. (그리드는 라디오 리스트 지원안함)
	gfn_setGridCombo(Grid1, "ds_com_list_2", 10);

	// 체크박스 리스트
	// 파라미터: 컨트롤명(문자열), 공통코드, 선택값, onClick시발생함수, left, top, width, 행열유무(row:행, col:열)
	// 주의)체크박스 리스트는 바인딩이 되지않는다
	gfn_SetCmnCheckBox("chk_test","P021", "200","chk_test_OnClick", 108, 171,120, "row");
}

//*------------------------------------------------------------------------------------------------
//* 함수설명     : 1.2 초기화 함수 
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//*-----------------------------------------------------------------------------------------------
function fn_reset()
{
	// dataset reset
	ds_grid_temp.ClearData();

	var vRow = ds_grid_temp.AddRow();

	// 기초값
	ds_grid_temp.SetColumn(vRow,"flag","I");	// 추가 flag 넣기
	ds_grid_temp.SetColumn(vRow,"row_id","");	// row_id(조회후 셋팅됨)
    ds_grid_temp.SetColumn(vRow,"operator", GDS_USER_INFO.eno);// 등록자, 수정자 넣기


	// 콤보의 기본값이 있다면 선택된 상태로 둔다.
	ds_grid_temp.SetColumn(vRow,"test_code","");
	ds_grid_temp.SetColumn(vRow,"code_sel","200");
	ds_grid_temp.SetColumn(vRow,"ds_sel","");
}


//*------------------------------------------------------------------------------------------------
//* 함수설명     : 1.3 조회 함수 
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//*-----------------------------------------------------------------------------------------------
function fn_search()
{

	svcID			= "test_template1.selectSample";
	url   			= "service::test_template1.selectSample.do";
	inDatasets  	= "";
	outDatasets 	= "ds_grid_temp=test_template1.selectSample ds_tap_test=test_template2.selectSample";
	argument    	= " _sqlName=test_template1.selectSample,test_template2.selectSample"
							+ " text_val=" + quote(s_edt_code_name.Text)
							+ " operator=" + quote(GDS_USER_INFO.eno)
						;
	callbackFunc 	= "gfn_callback";

	gfn_showProgressWindow(true);
	gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	 
}


//*------------------------------------------------------------------------------------------------
//* 함수설명     : 1.4 저장 함수 
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//*-----------------------------------------------------------------------------------------------
function fn_save()
{

	var bValidate = true;
	var tab2_bValidate = true;
	var pMessage = "";

	ds_grid_temp.FireEvent = false;		// DataSet filter 하더라도 Grid에 영향을 끼치지 않게.
	ds_tap_test.FireEvent = false;

	// 삭제된것도 포함되게Unfilter 한다.
	ds_grid_temp.UnFilter();
	ds_tap_test.UnFilter();

	ds_grid_temp.Filter("flag !='N'");	// 변경된것 모두 필터링  (저장/삭제) 
	ds_tap_test.Filter("flag !='N'");

	if(ds_grid_temp.rowcount > 0 || ds_tap_test.rowcount > 0)
	{
		// 그리드1 필수체크
		for(var i=0;i<ds_grid_temp.rowcount;i++)
		{
			if(length(ds_grid_temp.GetColumn(i, "must_input")) == 0)
			{
				bValidate = false;
				pMessage += "\n 그리드1-" + "["+i+"행] 누락:  필수항목";
			}
		}

		// 그리드2 필수체크
		for(var j=0;j<ds_tap_test.rowcount;j++)
		{
			if(length(ds_tap_test.GetColumn(j, "temp7")) == 0)
			{
				tab2_bValidate = false;
				pMessage += "\n 그리드2-" + "["+j+"행] 누락:  필수항목";
			}
		}		

		// 필수항목 누락 
		if(bValidate == false || tab2_bValidate == false)
		{
			gfn_popup_message("modal", "error", pMessage, "확인");
		}
		// 저장 
		else if(gfn_popup_message("modal", "confirm", "저장하시겠습니까?", "확인")) 
		{
			svcID		= "test_template1.transSample";              
			url   		= "service::test_template1.transSample.do";   
			inDatasets  = "test_template1.transSample=ds_grid_temp:A test_template2.transSample=ds_tap_test:A";   //U: 업데이트 된 row만 넘긴다, A:전부 전달, N:delete를 제외한 모든 정보전달                                      
			outDatasets = ""; 
			argument    = " _sqlName=test_template1.transSample,test_template2.transSample"
							;	  
			callbackFunc = "gfn_callback"; 

			gfn_showProgressWindow(true);
			gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	 
		}
	}
	else
	{
		gfn_popup_message("modal", "ico_alert", "저장할 내용이 없습니다.", "확인");	
	}

	ds_grid_temp.UnFilter();
	ds_tap_test.UnFilter();

	ds_grid_temp.Filter("flag != 'D'");	// 삭제된거는 계속 필터링
	ds_tap_test.Filter("flag != 'D'");

	ds_grid_temp.FireEvent = true;		// 이벤트 계속 사용 
	ds_tap_test.FireEvent = true;
}

//*------------------------------------------------------------------------------------------------
//* 함수설명     : 1.5 삭제 함수 
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//*-----------------------------------------------------------------------------------------------
function fn_delete()
{

	ds_grid_temp.FireEvent = false;		// DataSet filter 하더라도 Grid에 영향을 끼치지 않게.

	// 삭제된것도 포함되게Unfilter 한다.
	ds_grid_temp.UnFilter();

	ds_grid_temp.Filter("flag ='D'");	// 삭제된것 

	if(ds_grid_temp.rowcount > 0)
	{
		if(gfn_popup_message("modal", "confirm", "삭제하시겠습니까?", "확인")) 
		{
			svcID		= "test_template1.transSample";              
			url   		= "service::test_template1.transSample.do";   
			inDatasets  = "test_template1.transSample=ds_grid_temp:A";   //U: 업데이트 된 row만 넘긴다, A:전부 전달, N:delete를 제외한 모든 정보전달                                      
			outDatasets = ""; 
			argument    = " _sqlName=test_template1.transSample"
							;	  
			callbackFunc = "gfn_callback"; 

			gfn_showProgressWindow(true);
			gfn_Transaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	 
		}
	}
	else
	{
		gfn_popup_message("modal", "ico_alert", "삭제할 항목이 없습니다.", "확인");	
	}

	ds_grid_temp.UnFilter();
	ds_grid_temp.Filter("flag != 'D'");	// 삭제된거는 필터링

	ds_grid_temp.FireEvent = true;
}


//*------------------------------------------------------------------------------------------------
//* 함수설명     : //Transaction 호출후 결과값 확인 (gfn_callback에서 fn_callback을 호출한다.)
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//*-----------------------------------------------------------------------------------------------
function fn_callback(svcID, errorCode, errorMsg)
{

	switch(svcID) 
	{
		// 조회
		case "test_template1.selectSample" :

			// 조회 결과 없으면 1행 추가한다.
			if(ds_grid_temp.rowcount < 1) { fn_add_row();}

			//Grid1.SetColProp(0,"width",0);
			//Grid1.SetColProp(1,"width",0);
			//Grid1.SetColProp(2,"width",0);

		break;	

		// 저장/삭제 
		case "test_template.transSample" :
			fn_search();		
		break;	

		// Test DataSet 조회 
		case "test_template1.selectTest" :
		break;	

		// 탭2조회
		case "test_template2.selectSample" :

			// 조회 결과 없으면 1행 추가한다.
			if(ds_tap_test.rowcount < 1) { fn_add_row();}

			//Grid1.SetColProp(0,"width",0);
			//Grid1.SetColProp(1,"width",0);
			//Grid1.SetColProp(2,"width",0);

		break;	

		// 탭2저장/삭제 
		case "test_template2.transSample" :
			fn_search();		
		break;	

		default:
			  alert("기타"); 
	}
}


/**************************************************************************************************
  * 사용자 정의  Function 처리 부분
***************************************************************************************************/

//*------------------------------------------------------------------------------------------------
//* 함수설명     : 행추가
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//*-----------------------------------------------------------------------------------------------
function fn_add_row()
{



	switch(Tab0.TabIndex)
	{
		case 0:


			var vRow = ds_grid_temp.AddRow();

			// 기초값
			ds_grid_temp.SetColumn(vRow,"flag","I");	// 추가 flag 넣기
			ds_grid_temp.SetColumn(vRow,"row_id","");	// row_id(조회후 셋팅됨)
			ds_grid_temp.SetColumn(vRow,"operator", GDS_USER_INFO.eno);// 등록자, 수정자 넣기	

			// 콤보의 기본값이 있다면 선택된 상태로 둔다.
			ds_grid_temp.SetColumn(vRow,"test_code","");
			ds_grid_temp.SetColumn(vRow,"code_sel","200");
			ds_grid_temp.SetColumn(vRow,"ds_sel","");				
		break;

		case 1:

			var vRow = ds_tap_test.AddRow();

			// 기초값
			ds_tap_test.SetColumn(vRow,"flag","I");	// 추가 flag 넣기
			ds_tap_test.SetColumn(vRow,"row_id","");	// row_id(조회후 셋팅됨)
			ds_tap_test.SetColumn(vRow,"operator", GDS_USER_INFO.eno);// 등록자, 수정자 넣기			

		break;
	}





}


//*------------------------------------------------------------------------------------------------
//* 함수설명     : 행삭제
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//*-----------------------------------------------------------------------------------------------
function fn_delete_row()
{
	for(var i = ds_grid_temp.rowcount; i >= 0; i--)
	{	
		if(ds_grid_temp.GetColumn(i,"chk") == 1)
		{
			// 추가된것은 DB저장전이므로 그냥 DataSet에서 삭제
			if(ds_grid_temp.GetColumn(i,"flag") == "I")
			{
				ds_grid_temp.DeleteRow(i);
			}
			// 아닌것은 실제로 DB에 삭제해야되므로 flag를 D로 두고 마지막에 filter한다.
			else
			{
				ds_grid_temp.SetColumn(i,"flag", "D");
			}
		}
	}

	ds_grid_temp.Filter("flag != 'D'");	// 삭제된거는 필터링

}


//*------------------------------------------------------------------------------------------------
//* 함수설명     : 테스트 데이터셋 가져오기
