function fn_getTestData()
{

	svcID			= "test_template1.selectTest";
	url   			= "service::test_template1.selectTest.do";
	inDatasets  	= "";
	outDatasets 	= "ds_test=test_template1.selectTest";
	argument    	= " _sqlName=test_template1.selectTest"
							+ " COM_ID=" + quote("000")		
						;
	callbackFunc 	= "gfn_callback";

	gfn_SyncTransaction(svcID, url, inDatasets, outDatasets, argument, callbackFunc);	

}

