function pop_pop_name_OnClick(obj)
{
	var params = "p_code="+quote(edt_pop_code.Text) +
				 " p_value="+quote(edt_pop_name.Text);

	var retArr = gfn_openPopup(this, "sample::popup_sample",params , true);

	edt_pop_code.Text = retArr[0];
	edt_pop_name.Text = retArr[1];

}

//*------------------------------------------------------------------------------------------------
//* 함수설명     : DataSet 보기 
