//*-----------------------------------------------------------------------------------------------
function btn_view_OnClick(obj)
{
	alert(gfn_dsToString(ds_grid_temp));
}


//*------------------------------------------------------------------------------------------------
//* 함수설명     : Grid 해더 클릭시 
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//*-----------------------------------------------------------------------------------------------
function Grid1_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	switch(Tab0.TabIndex)
	{
		case 0:
			// 체크박스 클릭시
			if(nCell == 3)
			{
				gfn_SetCheckBoxAllSelected(nCell, tab0.tab1.Grid1, ds_grid_temp, "chk");
			}
			// 나머지는 Sorting
			else
			{
				gfn_GridSort(Grid1, ds_grid_temp, nCell);	
			}
			break;

		case 1:

			// 체크박스 클릭시
			if(nCell == 3)
			{
				gfn_SetCheckBoxAllSelected(nCell, tab0.tab2.Grid2, ds_tap_test, "chk");
			}

		break;			

	}
}


//*------------------------------------------------------------------------------------------------
//* 함수설명     : Grid 셀클릭시 Popup창 컨트롤 
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//*-----------------------------------------------------------------------------------------------
function Grid1_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{	
	switch(Tab0.TabIndex)
	{
		case 0:
			// 달력
			if(nCell == 4)
			{
				gfn_PopupGridCalendar(obj, nRow, nCell);
			}


			// 팝업
			if(nCell == 14)
			{
				var params = "p_code="+quote(edt_pop_code.Text) +
							 " p_value="+quote(edt_pop_name.Text);

				var retArr = gfn_openPopup(this, "sample::popup_sample",params , true);

				ds_grid_temp.SetColumn(nRow, "pop_code", retArr[0]);
				ds_grid_temp.SetColumn(nRow, "pop_name", retArr[1]);

			}

		break;

		case 1:

			// 달력
			if(nCell == 10)
			{
				gfn_PopupGridCalendar(obj, nRow, nCell);
			}	

		break;
	}
}

//*------------------------------------------------------------------------------------------------
//* 함수설명     : DataSet 변경시 flag 설정 
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//*-----------------------------------------------------------------------------------------------
function ds_grid_temp_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	// 체크박스 선택은 패스한다.
	if(ToUpper(strColumnID) == "CHK") return;

	// flag가 'N' 이면 'U' 로 바꾼다.
	// 즉 조회된거를 수정하면 U로 바꾼다.
	if(obj.GetColumn(nRow, "flag") == "N")
	{
		obj.SetColumn(nRow, "flag", "U");	// 수정했다는 표시 
	}
}

//*------------------------------------------------------------------------------------------------
//* 함수설명     : Grid Ctrl+A, Ctrl+C로 클립보드에 넣기  
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//*-----------------------------------------------------------------------------------------------
function Grid1_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	gfn_gridCopy(obj, ds_grid_temp, nChar, bCtrl);
}




//*------------------------------------------------------------------------------------------------
//* 함수설명     : Grid Ctrl+A, Ctrl+C로 클립보드에 넣기  
//* 함수유형     : 
//* parameter    : 
//* return value : 없음
//*-----------------------------------------------------------------------------------------------
function btn_cal_sel_OnClick(obj)
{
	gfn_SetCalendarDate(obj, "D", cal_saveDate);
}
